INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 13, 0, 1.9, 303400, 1, 270, 303400, "スナノヅチ", 0, 0, 1000, "【物理】生存競争激しい砂漠の\n頂点に君臨する、巨大トカゲ。\n毒を使い、砂を活かす戦法は、\n過酷な環境で得たサバイバル術。\n\n・敵全体に物理中ダメージを与え、\n　行動速度を下げる。\n　さらに、味方全体の物理攻撃力を\n　上げる。\n\n・前方1キャラを中心にした小範囲\n　に物理小ダメージを与え、毒状態\n　にする。さらに、物理防御力を\n　下げる。");
