INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 305300, 1, 270, 305300, "ハイ・ガルグ", 0, 0, 1000, "【物理】ルナの魔力を帯び変異した獰猛な魔獣。\n強靭な歯牙は歴戦の冒険者さえ容易く切り裂く。\n\n・ユニオンバーストは敵全体に物理大ダメージを\n　与え、さらに自分のＨＰを回復する。\n・後方範囲内の敵に物理ダメージを与え、スタン\n　させる。\n　さらに敵全体の物理防御力を中ダウンさせる。\n・前から３キャラに物理ダメージを与え、\n　毒状態にする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 305301, 1, 270, 305301, "アイスガルグ", 0, 0, 1000, "【物理】雪山に棲む獰猛な魔狼。\n強靭な歯牙は歴戦の冒険者さえ\n容易く氷塊の墓標へ変える。\n\n・ユニオンバーストは敵全体に物\n　理大ダメージを与える。\n\n・一番後ろの敵キャラを中心とし\n　て、範囲内の敵すべてに物理小\n　ダメージを与え、スタンさせる。\n　さらに敵全体の物理防御力を中\n　ダウンさせる。\n\n・前方範囲内の敵すべてに物理小\n　ダメージを与え、毒状態にする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 305302, 1, 270, 305301, "アイスガルグ", 0, 0, 1000, "【物理】雪山に棲む獰猛な魔狼。\n強靭な歯牙は歴戦の冒険者さえ\n容易く氷塊の墓標へ変える。\n\n・ユニオンバーストは敵全体に物\n　理大ダメージを与える。\n\n・一番後ろの敵キャラを中心とし\n　て、範囲内の敵すべてに物理小\n　ダメージを与え、スタンさせる。\n　さらに敵全体の物理防御力を中\n　ダウンさせる。\n\n・前方範囲内の敵すべてに物理小\n　ダメージを与え、毒状態にする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 305400, 1, 270, 305400, "アイスホーン", 0, 0, 1000, "【物理】強力な氷の頭突きで\n堅牢な盾も貫き穿つ一角獣。\n氷丘押し寄せる北の果てで、\n冒険者を氷獄へ突き落す。\n\n・敵全体に魔法大ダメージを与え、\n　魔法防御力を下げる。\n　さらに、味方全体のＴＰ上昇率を\n　上げる。\n\n・前方小範囲内に魔法小ダメージを\n　与え、氷結状態にする。さらに、\n　全体に魔法小ダメージを与え、\n　前方3キャラを小さく\n　吹き飛ばす。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 3.0, 305500, 2, 270, 305500, "フォギー", 0, 0, 2160, "【魔法】幾多の伝承に名を残す、記憶の大精霊。\n霧の如く実態を持たず、全て覆い隠してしまう。\n\n・ユニオンバーストは敵全体に\n　魔法大ダメージを与え、\n　さらに敵が呪い状態の場合、\n　ダメージがアップする。\n・敵全体に魔法中ダメージを与える。\n　さらに呪い状態にする。\n・分身体（紫）（赤）（青）のうち、\n　いずれか1体を召喚する。\n　召喚する際は敵全体をノックバックさせる。\n・【物理】分身体（紫）は、自身に物理/魔法\n　吸収バリアを展開する。\n・【物理】分身体（赤）は、敵全体に\n　魔法中ダメージを与える。\n・【物理】分身体（青）は、敵全体に\n　魔法中ダメージ。\n　さらに、呪い状態にする。\n　分身体（青）のＨＰが30％以下になった時、\n　フォギーのＴＰを300回復させる。\n・フォギー自身のＨＰが\n　50％を切ると、さらに攻撃が激しく\n　凶暴になる。");
