INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 307811, 2, 270, 307801, "雨神様", 0, 0, 3000, "【MODE1】悲嘆に暮れるアメきちは\nみんなの想いを拒絶し、心焦がれるほどに\n求めたハレきちの声すら今のアメきちには\n届かない。\n\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージ量に関係なく、攻撃を\n　命中させるたびに徐々に耐久性を失っていき、\n　200回攻撃を命中させるとバリアは破壊\n　される。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを\n　与えて吹き飛ばす。\n　さらに、前方範囲内の敵すべてにキャラの\n　最大ＨＰに応じた追加の魔法割合ダメージを\n　与える。\n・前方範囲内の敵すべてに\n　魔法中ダメージを与える。\n");
