INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 309001, 1, 270, 309001, "ランドスロース", 0, 0, 1000, "【物理】夢うつつの狭間で惰眠を貪るナマケモノ。\n眠りを妨げると怒り狂い、超能力で暴れまわる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体に魔法割合ダメージ\n・敵全体に物理／魔法攻撃力ダウン（必中）\n・敵全体にスタン（必中）\n【スキル１】\n・敵全体に魔法ダメージ\n・敵全体にスタン");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 309002, 1, 270, 309002, "レイジーママル", 0, 0, 1000, "【物理】広大な密林に棲む巨大な\n獣。身の危険が迫ると防衛反応が\n働き、大いなる野生を解き放つ。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた魔法\n　割合ダメージを与え、さらに物\n　理攻撃力と魔法攻撃力を大ダウ\n　ン。このスキルの状態異常はダ\n　メージを無効化しても付与され\n　る。\n\n・敵全体に魔法小ダメージを与え\n　スタンさせる。このスキルの状\n　態異常はダメージを無効化して\n　も付与される。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 309003, 1, 270, 309003, "ランドスロース", 0, 0, 1000, "【物理】夢うつつの狭間で惰眠を貪るナマケモノ。\n眠りを妨げると怒り狂い、超能力で暴れまわる。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に魔法割合ダメージ\n・敵全体に物理／魔法攻撃力ダウン（必中）\n・敵全体にスタン（必中）\n【スキル１】\n・敵全体に魔法ダメージ\n【通常攻撃】\n・前から敵１体に物理ダメージ");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 309004, 1, 270, 309004, "ランドスロース", 0, 0, 1000, "【物理】夢うつつの狭間で惰眠を貪るナマケモノ。\n眠りを妨げると怒り狂い、超能力で暴れまわる。\n\n・ランドスロースから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ランドスロースから受けるすべての状態異常と\n　ステータスダウン効果は、ダメージを無効化し\n　ても付与される。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与え、物理攻\n　撃力を特大ダウン、魔法攻撃力を中ダウンさせ、\n　スタン状態を付与する。\n・敵全体に魔法中ダメージを与え、スタン状態を\n　付与する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 309005, 1, 270, 309003, "ランドスロース", 0, 0, 1000, "【物理】夢うつつの狭間で惰眠を貪るナマケモノ。\n眠りを妨げると怒り狂い、超能力で暴れまわる。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ランドスロースから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与え、物理攻\n　撃力と魔法攻撃力を中ダウンさせ、スタン状態\n　を付与する。このスキルのステータスダウン効\n　果と状態異常は、ダメージを無効化しても付与\n　される。\n・敵全体に魔法中ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 309100, 1, 270, 309100, "ドロゲーター", 0, 0, 1000, "【物理】大河に棲む大ワニの魔物。\nマヌケな顔で、不思議な槍で大地を\n融解させ冒険者の動きを封じる。\n\n・敵全体に物理大ダメージを与え、\n　物理防御力と行動速度をダウン\n　させる。\n\n・前から３番目の敵を中心にした\n　小範囲に物理小ダメージを与え、\n　束縛状態にする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.8, 309101, 1, 270, 309101, "ドロゲーター", 0, 0, 3000, "【物理】大河に棲む大ワニの魔物。マヌケな顔で、\n不思議な槍で大地を融解させ敵の動きを封じる。\n\n・飛行状態の敵を攻撃対象にできる。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ドロゲーターから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵の挑発を無視して前か\n　ら３番目の敵を中心にした小範囲内の敵すべて\n　に物理特大ダメージを与える。さらに敵全体の\n　行動速度を中ダウンさせ、このスキルでダメー\n　ジを与えた敵の数に応じてＴＰをダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵の挑発を無視して、前から３番目の敵を中心\n　にした小範囲内の敵すべてに物理小ダメージを\n　与え、束縛状態を付与する。このスキルの状態\n　異常はダメージを無効化しても付与される。");
