INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 310501, 1, 270, 310500, "ラウドラパーン", 0, 0, 3000, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 310502, 1, 270, 310500, "ラウドラパーン", 0, 0, 3000, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 310503, 1, 270, 310500, "ラウドラパーン", 0, 0, 3000, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・ラウドラパーン自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 310504, 1, 270, 310500, "ラウドラパーン", 0, 0, 3000, "【ＭＯＤＥ１】ニュージェネレーションズの\nシャドウを召喚。森で出会った大ウサギ。\n３人は夢をあきらめない心を貫き通せるか、\n大ウサギは各個の弱さを見せつける。\n\n・一番前のキャラに物理ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 310505, 1, 270, 310500, "ラウドラパーン", 0, 0, 3000, "【ＭＯＤＥ２】森の人気者の歌声に魔物たちが\n大挙する。血沸き肉躍る野生の宴会が今、\n開幕する。\n\n・ブルーゴーレム、スリーピィオウル、\n　ツキツキを倒すと次のＭＯＤＥに移行する。\n\n・ラウドラパーンは、一番前のキャラに\n　物理ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 310506, 1, 270, 310500, "ラウドラパーン", 0, 0, 3000, "【ＭＯＤＥ３】宴も終焉、大兎が最後の力を振り\n絞る。咆哮にも似た壊震の歌声が、３人の夢道を\n阻む。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 310507, 1, 270, 310507, "バニフォルテ", 0, 0, 3000, "【物理】世にも奇妙な歌う大ウサギ\nの魔獣。その歌声はとても褒められ\nたものではなく、甚だ迷惑である。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた物理割\n　合ダメージを与え、ノックバック\n　させる。その後、目を回している\n　間、自身の物理防御力と魔法防御\n　力を特大ダウン。\n\n・一番前のキャラを中心として、\n　一定範囲に物理中ダメージを与\n　え、さらに行動速度を特大ダウン\n　とスタンさせる。\n\n・前から３キャラに物理小ダメージ\n　を与え、さらに物理防御力を大ダ\n　ウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310600, 1, 270, 310600, "葛藤のうえきちゃん", 310600, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310601, 1, 270, 310600, "葛藤のうえきちゃん", 310600, 0, 3000, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310602, 1, 270, 310600, "葛藤のうえきちゃん", 310600, 0, 3000, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310603, 1, 270, 310603, "葛藤のうえきちゃん", 310600, 0, 3000, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・うえきちゃん自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310604, 1, 270, 310604, "葛藤のうえきちゃん", 310600, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310605, 1, 270, 310604, "葛藤のうえきちゃん", 310600, 0, 3000, "【MODE１】終に極大化した謎のいきもの。\nまたしても如何ともし難い表情で、大滝の涙が\n大地を濡らしその涙は意思なく皆を蝕んでゆく。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・敵全体のＴＰを上昇させたのち、継続魔法ダメ\n　ージを与えるフィールドを展開する。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310606, 1, 270, 310604, "葛藤のうえきちゃん", 310600, 0, 3000, "【MODE２】冒険者の抵抗に怯んだか、両葉と\nその巨躯を晒しつつ佇む。だが、その表情は相も\n変わらず、如何ともし難い表情のまま。\n\n・全てのターゲットがブレイク無効。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 16, 0, 1.6, 310607, 1, 270, 310604, "葛藤のうえきちゃん", 310600, 0, 3000, "【MODE３】その変わらぬ表情は、終にも\n変わらず冒険者は、そのいきものの狂気と畏怖に\n慄く。果たしていきものの謎は解かれるのか…。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.4, 310700, 1, 270, 310700, "なかよしＸ", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.4, 310701, 1, 270, 310700, "なかよしＸ", 0, 0, 3000, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.4, 310702, 1, 270, 310700, "なかよしＸ", 0, 0, 3000, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.4, 310703, 1, 270, 310700, "なかよしＸ", 0, 0, 3000, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。\n・なかよしＸ自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 4.2, 310704, 1, 270, 310700, "なかよしＸ", 0, 0, 3000, "【ＭＯＤＥ１】【メルクリウス財団】の\nシャドウを召喚。自分たちの過ちを\n嘲笑うかのように、影の姿で皆を翻弄する。\n\n・自身が毒、猛毒、呪い、火傷の効果を受けると、\n　自身の物理防御力と魔法防御力と行動速度\n　が大ダウンする。\n・目の前の敵１キャラに物理ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.4, 310705, 1, 270, 310700, "なかよしＸ", 0, 0, 3000, "【ＭＯＤＥ２】鋼鉄の球体が、完璧なちぇる語で\n囁きかける。「ちぇる～ん」。心を掴み離さない\n洗脳の言の葉は、確実に世界をちぇるへと誘う。\n\n・ランダムな１キャラに魔法中ダメージを与え、\n　沈黙と誘惑状態にする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.4, 310706, 1, 270, 310700, "なかよしＸ", 0, 0, 3000, "【ＭＯＤＥ３】鋼鉄の絡繰が再び大地に立つ。\n諸刃の剣・ファイナルモードを解放し、\nなかよし部の３人を追い詰める。\n\n・自身が毒、猛毒、呪い、火傷の効果を受けると、\n　自身の物理防御力と魔法防御力と行動速度\n　が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.4, 310707, 1, 270, 310707, "なかよしＸ", 0, 0, 3000, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n・自身への継続ダメージ量アップ\n・自身に行動速度アップ\n【ユニオンバースト】\n・敵全体に魔法割合ダメージ\n【スキル１】\n・自身に物理攻撃力アップ\n・敵全体に物理ダメージ\n【スキル２】\n・前から敵３体に魔法割合ダメージ\n・前から敵３体に行動速度ダウン");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 310800, 1, 270, 310800, "忍びの頭領ハンゾウ", 0, 0, 3000, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 310801, 1, 270, 310800, "忍びの頭領ハンゾウ", 0, 0, 3000, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 310802, 1, 270, 310800, "忍びの頭領ハンゾウ", 0, 0, 3000, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。自身のＨＰが30%以下の場合、敵全体\n　を猛毒状態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。自身のＨＰ\n　が30%以下の場合、さらに敵全体をスタンさ\n　せる。\n・忍びの頭領ハンゾウ自身のＨＰが30％を切る\n　と、スキルの効果が変化する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 310803, 1, 270, 310800, "忍びの頭領ハンゾウ", 0, 0, 3000, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。自身のＨＰが30%以下の場合、敵全体\n　を猛毒状態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。自身のＨＰ\n　が30%以下の場合、さらに敵全体をスタンさ\n　せる。\n・忍びの頭領ハンゾウ自身のＨＰが30％を切る\n　と、スキルの効果が変化する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 310804, 1, 270, 310801, "忍びの頭領ハンゾウ", 0, 0, 3000, "【ＭＯＤＥ１】【牧場（エリザベスパーク）】の\nシャドウを召喚。幻術で造りだした四農士の幻影\nが皆の帰る場所を再び窮地に陥れる。\n\n・目の前の敵１キャラに物理ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 310805, 1, 270, 310800, "忍びの頭領ハンゾウ", 0, 0, 3000, "【ＭＯＤＥ２】秘伝の口寄せで忍蛙に化け、\nガマの猛毒をあたり一帯に噴霧する。その猛毒は\nひと吸いで全身を蝕み、徐々に躰を爛れ落とす。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して猛毒を吐き、敵全体を猛毒状\n　態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与え、さらに敵全体\n　をスタンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 310806, 1, 270, 310800, "忍びの頭領ハンゾウ", 0, 0, 3000, "【ＭＯＤＥ３】最期は、忍びとしての尊厳を取り\n戻し、己が真の力を発揮する。紫電一閃の早業に\n叫声を上げる間もなく屍と化す。\n\n・バトル開始直後に、自身のスキル威力をアップ\n　させる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して猛毒を吐き、敵全体を猛毒状\n　態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与え、さらに敵全体\n　をスタンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 0.1, 310900, 1, 270, 310900, "ランドソル各地に襲来した謎の円盤", 0, 0, 3000, "【物理】平和な収穫祭を急襲する、謎の飛行物体。\nランドソル各地に突如現れ、破壊と略奪を行う。\n\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方１キャラに物理小ダメージ。\n・残りＨＰの高い１キャラに物理小ダメージ。\n・ランダムな１キャラに物理小ダメージ。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311000, 1, 270, 311000, "トレントキャッスル", 0, 0, 3000, "【物理】塔の魔力で変異し要塞と化した巨大魔樹。\n大地の力を砲弾に変え、堅牢なその身を以って\n破壊の限りを尽くす。\n\n・敵全体に物理大ダメージを与える。\n・範囲内の敵すべてに継続魔法ダメージを与え\n　物理防御力を下げるフィールドを展開する。\n・後ろから２番目のキャラを中心とした小範囲内\n　に物理中ダメージを与え、ＴＰを下げる。\n　その後、味方全体のＴＰを回復する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311001, 1, 270, 311001, "トレントキャッスル", 0, 0, 3000, "【物理】深域の魔力で変異し要塞と化した魔樹。\n大地の力を砲弾に変え、破壊の限りを尽くす。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・敵全体にバリア解除\n・物理攻撃をする敵全体に物理ダメージ\n・自身の物理防御力が高いほど：ダメージアップ\n・魔法攻撃をする敵全体に物理ダメージ\n・自身の魔法防御力が高いほど：ダメージアップ\n・敵全体にスタン（必中）\n【スキル１】\n・前から３番目の敵と周囲に魔法ダメージ\n・前から３番目の敵を中心に物理防御力ダウン\n　フィールド\n・ダメージを与えた対象が少ないほど：ダウン量\n　上昇");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311002, 1, 270, 311002, "トレントキャッスル", 0, 0, 3000, "【物理】深域の魔力で変異し要塞と化した魔樹。\n大地の力を砲弾に変え、破壊の限りを尽くす。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・敵全体にバリア解除\n・物理攻撃をする敵全体に物理ダメージ\n・自身の物理防御力が高いほど：ダメージアップ\n・魔法攻撃をする敵全体に物理ダメージ\n・自身の魔法防御力が高いほど：ダメージアップ\n・敵全体にスタン（必中）\n【スキル１】\n・前から３番目の敵と周囲に魔法ダメージ\n・前から３番目の敵を中心に物理防御力ダウン\n　フィールド\n・ダメージを与えた対象が少ないほど：ダウン量\n　上昇");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.1, 311100, 1, 270, 311100, "黒の王ジャバウォック", 0, 0, 1000, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰをダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.1, 311101, 1, 270, 311100, "黒の王ジャバウォック", 0, 0, 1000, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.1, 311102, 1, 270, 311100, "黒の王ジャバウォック", 0, 0, 1000, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度をダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.1, 311103, 1, 270, 311100, "黒の王ジャバウォック", 0, 0, 1000, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度をダウンさせる。\n・黒の王ジャバウォック自身のＨＰが35％を切\n　ると、さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.1, 311104, 1, 270, 311101, "黒の王ジャバウォック", 0, 0, 1000, "【ＭＯＤＥ１】瘴黒の幕を身に纏い、黒の王が再\n臨する。闇と混沌を集約した障壁は、救世主の光\nの一射をも耐えるという。\n\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージを与えるたびに徐々に耐久性\n　を失っていき、2400000ダメージを与え\n　るとバリアは破壊される。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.1, 311105, 1, 270, 311100, "黒の王ジャバウォック", 0, 0, 1000, "【ＭＯＤＥ２】リノ、アユミ、ヒヨリのシャドウ\nを召喚。心の闇が具現化し、自己の弱さを露見さ\nせる。希望の心を取り戻せるか？今、問われる。\n\n・目の前の敵１キャラに物理ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.1, 311106, 1, 270, 311100, "黒の王ジャバウォック", 0, 0, 1000, "【ＭＯＤＥ３】黒の王が終の力を解放し、世界は\n常闇へと堕ちていく。本の殆どの頁は黒に染まり\n救世主の物語は終焉を迎えようとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰをダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311200, 1, 270, 311200, "フラワーシュバリエ", 0, 0, 3000, "【物理】大輪の花に擬態する魔物。\nその美しさに惹かれた、不用意な\n獲物の前に絶望の光が花開く。\n\n・前方３キャラに物理大ダメージを\n　与え、さらに敵全体に物理大\n　ダメージを与える。\n\n・前方２キャラに物理小ダメージを\n　与え、さらに自分の物理攻撃力を\n　中アップさせる。\n\n・前から１番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。\n\n・前から２番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311201, 1, 270, 311200, "フラワーシュバリエ", 0, 0, 3000, "【物理】大輪の花に擬態する魔物。\nその美しさに惹かれた、不用意な\n獲物の前に絶望の光が花開く。\n\n・前方３キャラに物理大ダメージを\n　与え、さらに敵全体に物理大\n　ダメージを与える。\n\n・前方２キャラに物理小ダメージを\n　与え、さらに自分の物理攻撃力を\n　中アップさせる。\n\n・前から１番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。\n\n・前から２番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311202, 1, 270, 311202, "フラワーシュバリエ", 0, 0, 3000, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・敵全体に魔法ダメージ（飛行効果無視）\n・与えた物理ダメージが高いほど：魔法ダメージ\n　量アップ\n【スキル１】\n・自身に物理攻撃力アップ\n・自身に物理／魔法防御力アップ\n・自身にＴＰ回復\n【通常攻撃】\n・前から１番目の敵と周囲の敵１体に物理\n　ダメージ");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311203, 1, 270, 311203, "フラワーシュバリエ", 0, 0, 3000, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・敵全体に魔法ダメージ（飛行効果無視）\n・与えた物理ダメージが高いほど：魔法ダメージ\n　量アップ\n【スキル１】\n・自身に物理攻撃力アップ\n・自身に物理／魔法防御力アップ\n・自身にＴＰ回復\n【通常攻撃】\n・前から１番目の敵と周囲の敵１体に物理\n　ダメージ");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311204, 1, 270, 311204, "フラワーシュバリエ", 0, 0, 3000, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n・環境効果【百花繚乱】を発動する。このスキル\n　の効果は敵、味方すべてに適用される。一定時\n　間が経過するごとに、敵、味方全体のＴＰを中\n　回復させる。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。\n・自身が一度の行動で受けるダメージ値を減衰さ\n　せる。ダメージ値が上昇するほど減衰率も上昇\n　する。\n・バトル開始時、敵全体にＴＰ上昇上限効果を付\n　与する。\n・フラワーシュバリエから受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に物理特大ダメージを与える。\n　その後、敵全体にこのスキルで与えたダメージ\n　量に応じた物理ダメージを与える。このスキル\n　による全体ダメージでは、ＴＰが回復しない。\n・自身の物理攻撃力と物理防御力と魔法防御力を\n　特大アップさせ、ＴＰを中回復する。\n・通常攻撃は、最も近い敵１キャラを中心にした\n　小範囲内の敵最大２キャラに物理特大ダメージ\n　を与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311300, 2, 270, 311300, "覚醒・浮気星", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311301, 2, 270, 311300, "覚醒・浮気星", 0, 0, 3000, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番後ろの敵を中心として、一定範囲に\n　魔法小ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法小ダメージを与える。\n　さらに誘惑状態にして物理攻撃力と魔法攻撃力\n　を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 3.0, 311302, 2, 270, 311300, "覚醒・浮気星", 0, 0, 3000, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番後ろの敵を中心として、一定範囲に\n　魔法小ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法小ダメージを与える。\n　さらに誘惑状態にして物理攻撃力を小アップ、\n　魔法攻撃力を中アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 3.0, 311303, 2, 270, 311300, "覚醒・浮気星", 0, 0, 3000, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・敵全体にキャラの最大ＨＰに応じた魔法割合\n　ダメージを与える。\n・一番後ろの敵を中心として、一定範囲に\n　魔法中ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を大アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法中ダメージを与える。\n　さらに誘惑状態にして物理攻撃力と魔法攻撃力\n　を大アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 3.0, 311304, 2, 270, 311300, "覚醒・浮気星", 0, 0, 3000, "【ＭＯＤＥ１】自身のハーレムドリームを築き\n上げるため手当たり次第、乙女たちを強制的に\n惑わす。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　ダメージを与えた後、浮気星が保持している\n　【ふた心】の数は０になる。\n・敵全体にキャラの最大ＨＰに応じた魔法割合\n　ダメージを与える。\n・挑発を無視して、最も後ろの敵１キャラに魔法\n　ダメージを与え、さらに引き寄せる。\n・一番前の敵を中心として、一定範囲に\n　魔法ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力をアップさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　このスキルの攻撃対象の数だけ、浮気星に\n　一定時間【ふた心】を追加する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 3.0, 311305, 2, 270, 311300, "覚醒・浮気星", 0, 0, 3000, "【ＭＯＤＥ２】綺羅星すらも霞ませると、己の\n輝きに自信をのぞかせ、その盲信と乙女への思い\nが銀河を巻き込み、全てを幻惑させる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが60％未満の場合、\n　さらに与えるダメージがアップする。\n　ダメージを与えた後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番前の敵を中心として、一定範囲に\n　魔法ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力をアップさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　このスキルの攻撃対象の数だけ、浮気星に\n　一定時間【ふた心】を追加する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 3.0, 311306, 2, 270, 311300, "覚醒・浮気星", 0, 0, 3000, "【ＭＯＤＥ３】夢見たハーレムが潰え、全てを\n無に還そうと真の力を解放する。銀河の星々を\n天の川に沈めるべく、全ての下心をブチ撒ける。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、バトル開始直後に、さらに与えるダメー\n　ジがアップする。\n　ダメージを与えた後、浮気星が保持している\n　【ふた心】の数は０になる。\n・敵全体にキャラの最大ＨＰに応じた魔法割合\n　ダメージを与える。\n・一番後ろの敵を中心として、一定範囲に\n　魔法ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力をアップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法ダメージを与える。\n　さらに誘惑状態にして物理攻撃力と魔法攻撃力\n　をアップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311400, 1, 270, 311400, "渚の暴君・鮫吉", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311401, 1, 270, 311400, "渚の暴君・鮫吉", 0, 0, 3000, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力をダウンさせ、物理攻撃力をアップさ\n　せる。\n・最も近い敵１キャラに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷、呪詛の効果を\n　受けていると、自身の物理防御力と魔法防御力\n　がアップする時間が短くなる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311402, 1, 270, 311400, "渚の暴君・鮫吉", 0, 0, 3000, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力をダウンさせ、物理攻撃力をアップさ\n　せる。\n・最も近い敵１キャラに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷、呪詛の効果を\n　受けていると、自身の物理防御力と魔法防御力\n　がアップする時間が短くなる。\n・鮫吉自身のＨＰが20％を切ると、敵全体に\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、一定時間、ユニオンバーストのみ使用で\n　きない状態にし、魔法攻撃力をダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311403, 1, 270, 311400, "渚の暴君・鮫吉", 0, 0, 3000, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力を大ダウンさせ、物理攻撃力をアップ\n　させる。\n・最も近い敵１キャラに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷、呪詛の効果を\n　受けていると、自身の物理防御力と魔法防御力\n　がアップする時間が短くなる。\n・鮫吉自身のＨＰが20％を切ると、敵全体に\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、一定時間、ユニオンバーストのみ使用で\n　きない状態にし、魔法攻撃力をダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311404, 1, 270, 311404, "渚の暴君・鮫吉", 0, 0, 3000, "【ＭＯＤＥ１】シマを荒らす不届きモノには、幾\n千もの抗争を潜り抜けた鋼の肉体から繰り出す、\n全身全霊のガチンコタックルをブチかます。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力をダウンさせ、物理攻撃力をアップさ\n　せる。\n・前方範囲内の敵すべてに物理大ダメージを与え、\n　物理防御力を大ダウンさせる。\n・前方３キャラに物理大ダメージを与える。さら\n　に、自身にも物理ダメージを与え、物理防御力\n　と魔法防御力を一定時間極大ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311405, 1, 270, 311405, "渚の暴君・鮫吉", 0, 0, 3000, "【ＭＯＤＥ２】圧倒的なカリスマ性と絶望的な暴\n力につき従う、舎弟たちが集結する。海洋族の精\n鋭によるカチコミが、いま幕を開ける。\n\n・鮫吉は目の前の敵１キャラへの物理攻撃を行う。\n・前方３キャラに物理大ダメージを与える。さら\n　に、自身にも物理ダメージを与え、物理防御力\n　と魔法防御力を一定時間極大ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311406, 1, 270, 311404, "渚の暴君・鮫吉", 0, 0, 3000, "【ＭＯＤＥ３】獰猛な雄叫びとともに、暴君は追\n撃を繰り出す。刃向かう者を完膚なきまでに殴り\n潰すまで、降り注ぐ轟拳の嵐は決してやまない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力を大ダウンさせ、物理攻撃力をアップ\n　させる。\n・前方範囲内の敵すべてに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷の効果を受けてい\n　ると、自身の物理防御力と魔法防御力がアップ\n　する時間が短くなる。\n・鮫吉自身のＨＰが10％を切ると、敵全体に\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、一定時間、ユニオンバーストのみ使用で\n　きない状態にし、魔法攻撃力をダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311407, 1, 270, 311407, "レイジングジョー", 0, 0, 3000, "【物理】塔の魔力で狂化した、海洋族の狂戦士。\n獰猛な雄叫びとともに、猛々しく拳撃を繰り出す。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力を大ダウンさせ、さらに物理攻撃力を\n　中アップさせる。\n・最も近い１キャラを中心とした小範囲内の敵す\n　べてに物理大ダメージを与え、物理防御力を大\n　ダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　レイジングジョーが毒、猛毒、呪い、呪詛、火\n　傷の効果を受けていると、自身の物理防御力と\n　魔法防御力がアップする時間が短くなる。\n・レイジングジョー自身のＨＰが40％を切ると、\n　敵全体にキャラの最大ＨＰに応じた物理割合ダ\n　メージを与え、一定時間、ユニオンバーストの\n　み使用できない状態にする。\n　さらにバトル中、魔法攻撃力を大ダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311408, 1, 270, 311408, "レイジングジョー", 0, 0, 3000, "【物理】深域の魔力で狂化した、海洋族の狂戦士。\n獰猛な雄叫びとともに、猛々しく拳撃を繰り出す。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から敵１体に物理ダメージ（召喚体無視）\n・敵全体に物理ダメージ（召喚体無視・ＴＰ回復\n　不可）\n・このスキルで与えたダメージが大きいほど：ダ\n　メージアップ\n【スキル１】\n・前から敵１体に物理ダメージ\n・前から敵１体に物理防御力ダウン（必中）\n【通常攻撃】\n・前から敵１体に物理ダメージ");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.8, 311409, 1, 270, 311409, "渚の暴君・鮫吉", 0, 0, 3000, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に物理割合ダメージ（ＴＰ回復不可）\n・敵全体に物理防御力ダウン（必中）\n・自身に物理攻撃力アップ\n【スキル１】\n・前方範囲に物理ダメージ\n・前方範囲に物理防御力ダウン（必中）\n【スキル２】\n・敵全体をノックバック\n・自身の物理／魔法防御力アップ\n・自身が継続ダメージ状態の場合：\n　持続時間ダウン\n【通常攻撃】\n・前から敵１体に物理ダメージ\n【ＨＰ20％以下時発動】\n・敵全体に物理割合ダメージ（ＴＰ回復不可）\n・敵全体に魔法攻撃力ダウン（必中）\n・敵全体にＵＢ封印（必中）\n・自身に物理攻撃力アップ");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.4, 311500, 2, 270, 311500, "暴走のイノセントボウ", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.4, 311501, 2, 270, 311500, "暴走のイノセントボウ", 0, 0, 3000, "【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに最大ＨＰに応じた魔法\n　割合ダメージを与え、さらにＨＰが60％を\n　切ったキャラを一定時間エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.4, 311502, 2, 270, 311500, "暴走のイノセントボウ", 0, 0, 3000, "【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに最大ＨＰに応じた魔法\n　割合ダメージを与え、さらにＨＰが60％を\n　切ったキャラを一定時間エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度を中ダウンさせる。\n・イノセントボウ自身のＨＰが50％を切ると、\n　魔法攻撃力が小アップし、さらに攻撃が激しく\n　凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.4, 311503, 2, 270, 311500, "暴走のイノセントボウ", 0, 0, 3000, "【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに最大ＨＰに応じた魔法\n　割合ダメージを与え、さらにＨＰが60％を\n　切ったキャラを一定時間エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度を中ダウンさせる。\n・イノセントボウ自身のＨＰが50％を切ると、\n　魔法攻撃力とＴＰが中アップし、さらに攻撃が\n　激しく凶暴になる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.4, 311504, 2, 270, 311500, "暴走のイノセントボウ", 0, 0, 3000, "【ＭＯＤＥ１】歪みし願いは結晶となり、人々を\n狂わせる。その結晶はすべての進攻を封殺する。\n\n・バトル開始時に歪願のクリスタルを呼び出す。\n・歪願のクリスタルがバトル中に存在している間、\n　イノセントボウの物理防御力と魔法防御力を大\n　幅にアップさせる。\n・歪願のクリスタルが破壊されると、イノセント\n　ボウの物理防御力と魔法防御力を大幅にダウン\n　させる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度をダウンさせ、さらに魔法防御力を\n　ダウンさせる。");
