INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.7, 312600, 1, 270, 312600, "ジェネラルホーン", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.7, 312601, 1, 270, 312600, "ジェネラルホーン", 0, 0, 3000, "【物理】立派な麋角をもつ大型魔物。大岩をも\n軽々粉砕する大角で冒険者をまとめて薙ぎ払う。\n\n・通常攻撃は、前方１キャラを中心とした範囲内\n　に物理小ダメージを与える。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。\n　さらに、自身の物理・魔法防御力を中ダウンさ\n　せる。\n・前方範囲内に物理中ダメージを与え、麻痺さ\n　せる。さらに、自身の物理・魔法防御力を大ダ\n　ウンさせる。\n　このスキルの状態異常はダメージを無効化\n　しても付与される。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 2.7, 312602, 1, 270, 312600, "ジェネラルホーン", 0, 0, 3000, "【物理】立派な麋角をもつ大型魔物。大岩をも\n軽々粉砕する大角で冒険者をまとめて薙ぎ払う。\n\n・通常攻撃は、前方１キャラを中心とした範囲内\n　に物理小ダメージを与える。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。\n　さらに、自身の物理・魔法防御力を中ダウンさ\n　せる。\n・前方範囲内に物理中ダメージを与え、麻痺さ\n　せる。さらに、自身の物理・魔法防御力を大ダ\n　ウンさせる。\n　このスキルの状態異常はダメージを無効化\n　しても付与される。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。");
