INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316303, 1, 270, 316300, "海将タツノココマンダー", 0, 0, 3000, "【物理】強靭な肉体で三叉槍を操る海獣。人間に\n捕われた怒りで暴れ回り、ビーチを恐怖に染める。\n\n・ユニオンバーストは、前から３番目のキャラに\n　魔法大ダメージを与え、前から３番目のキャラ\n　を中心にした小範囲の敵を気絶させる。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・自身の魔法攻撃力を大アップさせ、さらにＴＰ\n　を中回復する。\n・前から３番目のキャラに物理中ダメージを与え\n　る。\n・海将タツノココマンダーのＨＰが50％以下に\n　なると攻撃が激しく凶暴になる代わりに物理防\n　御力が大ダウンする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316304, 1, 270, 316304, "海将タツノココマンダー", 0, 0, 3000, "【ＭＯＤＥ１】海将の圧倒的な力は、三叉槍を\n一振りすれば忽ちに激流を生み、人々を襲う。\n勇気を奮い立たせ、猛然たる水流に立ち向かえ！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・海将タツノココマンダーから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回復\n　量を中ダウンさせる。\n・ユニオンバーストは、挑発を無視して、前から\n　３番目のキャラに魔法大ダメージを与え、前か\n　ら３番目のキャラを中心にした小範囲の敵を気\n　絶させる。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・敵全体に魔法中ダメージを与え、さらに魔法防\n　御力を大ダウンさせて大きく吹き飛ばす。その\n　後、自身のＴＰを大回復する。\n・挑発を無視して、前から３番目のキャラに物理\n　中ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316305, 1, 270, 316305, "海将タツノココマンダー", 0, 0, 3000, "【ＭＯＤＥ２】海将の怒りで渚に沸き立つ蜃気楼\nより、少女たちの幻影が襲い来る。ひと夏に賭け\nた想いを貫くため、いざ試練を乗り越えん！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・海将タツノココマンダーから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回復\n　量を中ダウンさせる。\n・ヒヨリ、レイ、ユイのシャドウを召喚。\n・前から３番目のキャラに物理中ダメージを与え\n　る。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316306, 1, 270, 316305, "海将タツノココマンダー", 0, 0, 3000, "【ＭＯＤＥ３】憤怒は最高潮を迎え、海将は全身\n全霊を賭した猛攻を見せる。平穏を取り戻すため\n秘めたる愛と友情を力に変えて強敵を打ち破れ！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・海将タツノココマンダーから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回復\n　量を中ダウンさせる。\n・ユニオンバーストは、前から３番目のキャラに\n　魔法大ダメージを与え、前から３番目のキャラ\n　を中心にした小範囲の敵を気絶させる。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・自身の魔法攻撃力を大アップさせ、さらにＴＰ\n　を中回復する。\n・前から３番目のキャラに物理中ダメージを与え\n　る。\n・海将タツノココマンダーのＨＰが50％以下に\n　なると攻撃が激しく凶暴になる代わりに物理防\n　御力が大ダウンする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316400, 2, 270, 316400, "ムーンレイゲート", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316401, 2, 270, 316400, "ムーンレイゲート", 0, 0, 3000, "【魔法】月の満ち欠けに応じて現れる、異なる二\nつの世界を繋ぐゲート。みゅ～ちゃんをゲートま\nで導いて、元の世界に戻してあげよう！\n\n・ユニオンバーストは、敵全体にムーンレイゲー\n　トの【月の光印】の数に応じた魔法割合ダメー\n　ジを与える。\n・敵全体をノックバックさせ行動速度を中ダウン\n　させる。さらに、敵全体にムーンレイゲートの\n　【月の光印】の数に応じた魔法割合ダメージを\n　与える。\n　その後ムーンレイゲートに【月の光印】を10\n　個付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体にムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n　さらにムーンレイゲートを中心とした範囲内の\n　敵全てにムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n・【月の光印】は最大50個まで付与される。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316402, 2, 270, 316400, "ムーンレイゲート", 0, 0, 3000, "【魔法】月の満ち欠けに応じて現れる、異なる二\nつの世界を繋ぐゲート。みゅ～ちゃんをゲートま\nで導いて、元の世界に戻してあげよう！\n\n・ユニオンバーストは、敵全体にムーンレイゲー\n　トの【月の光印】の数に応じた魔法割合ダメー\n　ジを与える。\n・敵全体をノックバックさせ行動速度を中ダウン\n　させる。さらに、敵全体にムーンレイゲートの\n　【月の光印】の数に応じた魔法割合ダメージを\n　与える。\n　その後ムーンレイゲートに【月の光印】を10\n　個付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体にムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n　さらにムーンレイゲートを中心とした範囲内の\n　敵全てにムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n・【月の光印】は最大50個まで付与される。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316403, 2, 270, 316400, "ムーンレイゲート", 0, 0, 3000, "【魔法】月の満ち欠けに応じて現れる、異なる二\nつの世界を繋ぐゲート。みゅ～ちゃんをゲートま\nで導いて、元の世界に戻してあげよう！\n\n・ユニオンバーストは、敵全体にムーンレイゲー\n　トの【月の光印】の数に応じた魔法割合ダメー\n　ジを与える。\n・敵全体をノックバックさせ行動速度を中ダウン\n　させる。さらに、敵全体にムーンレイゲートの\n　【月の光印】の数に応じた魔法割合ダメージを\n　与える。\n　その後ムーンレイゲートに【月の光印】を10\n　個付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体にムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n　さらにムーンレイゲートを中心とした範囲内の\n　敵全てにムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n・残り体力が50％を切るとムーンレイゲートの\n　物理防御力、魔法防御力を中アップさせる。\n　さらにムーンレイゲートに【月の光印】を20\n　個付与し、行動パターンが変化する。\n・【月の光印】は最大50個まで付与される。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316404, 2, 270, 316406, "ムーンレイゲート", 0, 0, 3000, "【ＭＯＤＥ１】ひと夏を共に過ごしたみゅ～ちゃ\nんへの哀惜の念が生んだ、【リトルリリカル】の\n影。それは無慈悲にもゲートへの道を立ち塞ぐ。\n\n・バトル開始時、ムーンレイゲートの物理防御力\n　と魔法防御力を小アップさせる。この防御力ア\n　ップ効果で上昇した防御力は、バトル中に受け\n　る防御力ダウンスキルの影響を受けない。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316405, 2, 270, 316405, "ムーンレイゲート", 0, 0, 3000, "【ＭＯＤＥ２】門を通して滲み伝わる、彼方の世\n界の魔力で強化された海の魔物。圧倒的な魔力の\n奔流が、少女たちにさらなる試練を与える。\n\n・バトル開始時、ムーンレイゲートの物理防御力\n　と魔法防御力を小アップさせる。この防御力ア\n　ップ効果で上昇した防御力は、バトル中に受け\n　る防御力ダウンスキルの影響を受けない。\n・このバトルのモンスターから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回復\n　量を中ダウンさせる。\n・ムーンレイゲート以外の味方の物理攻撃力、魔\n　法攻撃力を中アップさせる。\n・ムーンレイゲートがダメージを受けると、ムー\n　ンレイゲート以外の味方の物理防御力、魔法防\n　御力を極大ダウンさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316406, 2, 270, 316404, "ムーンレイゲート", 0, 0, 3000, "【ＭＯＤＥ３】二つの世界を繋ぐ最後のゲート。\nかけがえのないひと夏の思い出を胸に、小さき友\nを煌めく月光の彼方へと導こう！\n\n・バトル開始時、ムーンレイゲートの物理防御力\n　と魔法防御力を小アップさせる。この防御力ア\n　ップ効果で上昇した防御力は、バトル中に受け\n　る防御力ダウンスキルの影響を受けない。\n・ムーンレイゲートから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、敵全体にムーンレイゲー\n　トの【月の光印】の数に応じた魔法割合ダメー\n　ジを与える。\n・敵全体をノックバックさせ行動速度を中ダウン\n　させる。さらに、敵全体にムーンレイゲートの\n　【月の光印】の数に応じた魔法割合ダメージを\n　与える。\n　その後ムーンレイゲートに【月の光印】を15\n　個付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体にムーンレイゲートの【月の光印】の数\n　に応じた魔法中ダメージを与える。\n　さらにムーンレイゲートを中心とした範囲内の\n　敵全てにムーンレイゲートの【月の光印】の数\n　に応じた魔法大ダメージを与える。\n・残り体力が40％を切るとムーンレイゲートの\n　物理防御力、魔法防御力を中アップさせる。\n　さらにムーンレイゲートに【月の光印】を40\n　個付与し、行動パターンが変化する。\n・【月の光印】は最大70個まで付与される。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316500, 1, 270, 316500, "哀執のバロメッツ", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316501, 1, 270, 316500, "哀執のバロメッツ", 0, 0, 3000, "【物理】最果ての大樹に住まう伝説の植物羊。老\n人との再会を望み続けたが、その長い時間が彼を\n追い詰め他者を拒絶する魔獣へと変えてしまった。\n\n・哀執のバロメッツに毎秒【憂いの棘】を３つ追\n　加する。\n・哀執のバロメッツがダメージを受けるたびに哀\n　執のバロメッツの【憂いの棘】を１つ減少させ\n　る。\n・ユニオンバーストは、敵全体のバリアを解除し\n　敵全体に哀執のバロメッツの【憂いの棘】の数\n　に応じた物理割合ダメージを与える。\n・残りＨＰ割合が少ない敵３キャラに哀執のバロ\n　メッツの【憂いの棘】の数に応じた物理割合ダ\n　メージを与える。\n・前方３キャラに哀執のバロメッツの【憂いの\n　棘】の数に応じた物理割合ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316502, 1, 270, 316500, "哀執のバロメッツ", 0, 0, 3000, "【物理】最果ての大樹に住まう伝説の植物羊。老\n人との再会を望み続けたが、その長い時間が彼を\n追い詰め他者を拒絶する魔獣へと変えてしまった。\n\n・哀執のバロメッツに毎秒【憂いの棘】を３つ追\n　加する。\n・哀執のバロメッツがダメージを受けるたびに哀\n　執のバロメッツの【憂いの棘】を１つ減少させ\n　る。\n・ユニオンバーストは、敵全体のバリアを解除し\n　敵全体に哀執のバロメッツの【憂いの棘】の数\n　に応じた物理割合ダメージを与える。\n・残りＨＰ割合が少ない敵３キャラに哀執のバロ\n　メッツの【憂いの棘】の数に応じた物理割合ダ\n　メージを与える。\n・前方３キャラに哀執のバロメッツの【憂いの\n　棘】の数に応じた物理割合ダメージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316503, 1, 270, 316500, "哀執のバロメッツ", 0, 0, 3000, "【物理】最果ての大樹に住まう伝説の植物羊。老\n人との再会を望み続けたが、その長い時間が彼を\n追い詰め他者を拒絶する魔獣へと変えてしまった。\n\n・哀執のバロメッツに毎秒【憂いの棘】を４つ追\n　加する。\n・哀執のバロメッツがダメージを受けるたびに哀\n　執のバロメッツの【憂いの棘】を１つ減少させ\n　る。\n・ユニオンバーストは、敵全体のバリアを解除し\n　敵全体に哀執のバロメッツの【憂いの棘】の数\n　に応じた物理割合ダメージを与える。\n・残りＨＰ割合が少ない敵３キャラに哀執のバロ\n　メッツの【憂いの棘】の数に応じた物理割合ダ\n　メージを与える。\n・前方３キャラに哀執のバロメッツの【憂いの\n　棘】の数に応じた物理割合ダメージを与える。\n・残り体力が25％を切ると哀執のバロメッツ\n　に【憂いの棘】を35個付与する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316504, 1, 270, 316504, "哀執のバロメッツ", 0, 0, 3000, "【ＭＯＤＥ１】孤独と猜疑と言う名の被毛に閉じ\n籠った獣を守るが如く、森の魔物たちが冒険者の\n前に立ち塞がる。魔物を制し道を切り開け。\n\n・バトル開始時、哀執のバロメッツの物理防御力\n　と魔法防御力を小アップさせる。この防御力\n　アップ効果で上昇した防御力は、バトル中に受\n　ける防御力ダウンスキルの影響を受けない。\n・哀執のバロメッツとお供のモンスターから受け\n　るすべてのダメージで発生する、パーティキャ\n　ラのＴＰ回復量を中ダウンさせる。\n・哀執のバロメッツは環境効果によるダメージを\n　受けない。\n・残りＨＰ割合が少ない敵３キャラに物理割合ダ\n　メージを与える。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316505, 1, 270, 316505, "哀執のバロメッツ", 0, 0, 3000, "【ＭＯＤＥ２】哀しき羊は被毛の鎧を硬化させ\nあらゆるものを拒絶する。刃も拳も魔法も\n差し伸べられた手でさえも。\n\n・バトル開始時、哀執のバロメッツの物理防御力\n　を中アップさせ、魔法防御力を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・哀執のバロメッツから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、敵全体のバリアを解除し\n　物理割合ダメージを与える。哀執のバロメッツ\n　が毒、猛毒、呪い、火傷、呪詛の効果を受けて\n　いると、与えるダメージがダウンする。\n・残りＨＰ割合が少ない敵３キャラに物理大\n　ダメージを与える。哀執のバロメッツが毒、猛\n　毒、呪い、火傷、呪詛の効果を受けている\n　と、与えるダメージがダウンする。\n・哀執のバロメッツが毒、猛毒、呪い、火傷、呪\n　詛の効果を受けると、哀執のバロメッツの\n　物理・魔法防御力が特大ダウンする。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316506, 1, 270, 316506, "哀執のバロメッツ", 0, 0, 3000, "【ＭＯＤＥ３】老旅人と結んだ約束と臆病な心の\n間で揺れる葛藤は、憂いの棘となり狂気を生む。\nその暴威に立ち向かい、かの獣に想いを届けろ。\n\n・バトル開始時、哀執のバロメッツの物理防御力\n　と魔法防御力を小アップさせる。この防御力\n　アップ効果で上昇した防御力は、バトル中に受\n　ける防御力ダウンスキルの影響を受けない。\n・哀執のバロメッツから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・哀執のバロメッツに毎秒【憂いの棘】を４つ追\n　加する。\n・哀執のバロメッツがダメージを受けるたびに哀\n　執のバロメッツの【憂いの棘】を１つ減少させ\n　る。\n・ユニオンバーストは、敵全体のバリアを解除し\n　敵全体に哀執のバロメッツの【憂いの棘】の数\n　に応じた物理割合ダメージを与える。\n・残りＨＰ割合が少ない敵３キャラに哀執のバロ\n　メッツの【憂いの棘】の数に応じた物理割合ダ\n　メージを与える。\n・前方３キャラに哀執のバロメッツの【憂いの\n　棘】の数に応じた物理割合ダメージを与える。\n・残り体力が25％を切ると哀執のバロメッツ\n　に【憂いの棘】を20個付与する。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316507, 1, 270, 316507, "哀執のバロメッツ", 0, 0, 3000, "【物理】最果ての大樹に住まう伝説の植物羊。老\n人との再会を望み続けたが、その長い時間が彼を\n追い詰め他者を拒絶する魔獣へと変えてしまった。\n\n【特殊効果】\n・自身に【憂いの棘】を毎秒４個追加\n・自身がダメージを受けた時：\n　自身の【憂いの棘】を１個減少\n・自身の【憂いの棘】の数が多いほど：各スキル\n　を強化\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体にバリア解除\n・敵全体に物理割合ダメージ\n【スキル１】\n・残りＨＰ割合が最も低い敵３体に物理割合\n　ダメージ\n【スキル２】\n・前から敵３体に物理割合ダメージ\n【ＨＰ25％以下時発動】\n・自身に【憂いの棘】を20個追加");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 6, 0, 1.5, 316600, 1, 270, 316600, "ゴブリンライダー", 0, 0, 1000, "【物理】人竜一体となって戦うゴブリン騎手。\n敏速に空を駆け巡り、地上の敵に襲いかかる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・後ろから敵３体に物理ダメージ\n・後ろから敵３体にスタン（必中）\n【スキル１】\n・後ろから敵２体に物理ダメージ\n・後ろから敵２体に物理防御力ダウン（必中）\n【通常攻撃】\n・後ろから敵１体に物理ダメージ（挑発無視）");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 6, 0, 1.5, 316601, 1, 270, 316601, "ゴブリンライダー", 0, 0, 1000, "【物理】人竜一体となって戦うゴブリン騎手。\n敏速に空を駆け巡り、地上の敵に襲いかかる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウンカット\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・後ろから敵３体に物理ダメージ\n・後ろから敵３体にスタン（必中）\n【スキル１】\n・後ろから敵２体に物理ダメージ\n・後ろから敵２体に物理防御力ダウン（必中）\n・後ろから敵２体にＴＰダウン（必中）\n【通常攻撃】\n・後ろから敵１体に物理ダメージ（挑発無視）");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 6, 0, 1.5, 316602, 1, 270, 316602, "ゴブリンライダー", 0, 0, 1000, "【物理】人竜一体となって戦うゴブリン騎手。\n敏速に空を駆け巡り、地上の敵に襲いかかる\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・後ろから敵３体に物理ダメージ（召喚体無視）\n・後ろから敵３体にスタン（召喚体無視・必中）\n【スキル１】\n・後ろから敵２体に物理ダメージ（召喚体無視）\n・後ろから敵２体に物理防御力ダウン（召喚体無\n　視・必中）\n【通常攻撃】\n・後ろから敵１体に物理ダメージ（挑発無視・\n　ＴＰ回復不可）");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 6, 0, 1.5, 316603, 1, 270, 316603, "ゴブリンライダー", 0, 0, 1000, "【物理】人竜一体となって戦うゴブリン騎手。\n敏速に空を駆け巡り、地上の敵に襲いかかる。\n\n・ゴブリンライダーから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を特\n　大ダウンさせる。\n・ゴブリンライダーから受けるすべての状態異常\n　とステータスダウン効果は、ダメージを無効化\n　しても付与される。\n・ユニオンバーストは、最も遠い敵３キャラに物\n　理中ダメージを与え、スタン状態を付与する。\n・最も遠い敵２キャラに物理小ダメージを与え、\n　物理防御力とＴＰを大ダウンさせる。このスキ\n　ルはダメージを無効化してもＴＰをダウンさせ\n　る。\n・通常攻撃は、敵の挑発を無視して最も遠い敵１\n　キャラに物理中ダメージを与える。このスキル\n　によるダメージでは、ＴＰが回復しない。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316700, 1, 270, 316700, "狂乱の配信者・カガミ", 0, 0, 3000, "");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 16, 0, 1.6, 316701, 1, 270, 316700, "狂乱の配信者・カガミ", 0, 0, 3000, "【物理】自身の存在を絶対的なものにするべく、\n狂気を重ねる配信者。欲望にとり憑かれ、純粋な\n想いを持つ者たちに刃を向ける。\n\n・バトル開始時、狂乱の配信者・カガミに【いい\n　ね】を５個付与する。\n・ユニオンバーストは、目の前の敵１キャラに物\n　理特大ダメージを与える。狂乱の配信者・カガ\n　ミの【いいね】の数に応じてダメージ量がアッ\n　プする。さらに【いいね】を15個付与する。\n・狂乱の配信者・カガミの物理防御力と魔法防御\n　力を特大ダウンし、狂乱の配信者・カガミに\n　【いいね】を10個付与する。さらに、【いい\n　ね】の数に応じて自身の物理攻撃力とＴＰを\n　アップする。\n・前方２キャラに物理大ダメージを与え、狂乱の\n　配信者・カガミに【いいね】を５個付与する。\n・狂乱の配信者・カガミが毒、猛毒、呪い、火\n　傷、呪詛の効果を受けると、狂乱の配信者・\n　カガミの【いいね】が５個減少する。");
