INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (1.8, "【物理】強力な氷の頭突きで\n堅牢な盾も貫き穿つ一角獣。\n氷丘押し寄せる北の果てで、\n冒険者を氷獄へ突き落す。\n\n・敵全体に魔法大ダメージを与え、\n　魔法防御力を下げる。\n　さらに、味方全体のＴＰ上昇率を\n　上げる。\n\n・前方小範囲内に魔法小ダメージを\n　与え、氷結状態にする。さらに、\n　全体に魔法小ダメージを与え、\n　前方3キャラを小さく\n　吹き飛ばす。", 16, "アイスホーン", 305400, 1000, 0, 270, 1, 0, 0, 0, 305400);
