INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0.1, "【物理】平和な収穫祭を急襲する、謎の飛行物体。\nランドソル各地に突如現れ、破壊と略奪を行う。\n\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方１キャラに物理小ダメージ。\n・残りＨＰの高い１キャラに物理小ダメージ。\n・ランダムな１キャラに物理小ダメージ。", 16, "ランドソル各地に襲来した謎の円盤", 310900, 3000, 0, 270, 1, 0, 0, 0, 310900);
