INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (2.8, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力をダウンさせ、物理攻撃力をアップさ\n　せる。\n・最も近い敵１キャラに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷、呪詛の効果を\n　受けていると、自身の物理防御力と魔法防御力\n　がアップする時間が短くなる。", 16, "渚の暴君・鮫吉", 311401, 3000, 0, 270, 1, 0, 0, 0, 311400);
