INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (2.4, "【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに最大ＨＰに応じた魔法\n　割合ダメージを与え、さらにＨＰが60％を\n　切ったキャラを一定時間エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。", 16, "暴走のイノセントボウ", 311501, 3000, 0, 270, 2, 0, 0, 0, 311500);
