INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (2.7, "【ＭＯＤＥ１】第一の試練。黒き風の精霊の暴風\nの継ぎ目を見切れば、自ずと己の進むべき道が\n切り拓かれん。\n\n・敵全体に物理ダメージとスタンを与え、ノック\n　バックさせる。\n　黒風の精霊が眠っている間、自身の物理防御力\n　が大幅にダウンし、その間に100000以上\n　のダメージを与えると、防御力ダウンが延長さ\n　れ行動速度もダウンする。", 16, "黒風の精霊", 312704, 3000, 0, 270, 2, 0, 0, 0, 312700);
