INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ワイズワンド", 1080, 0, 7, 206100, 4.5, 0, 2, 0, 270, "【魔法】遠距離攻撃\n浴びれば凍える冷気の魔法で、\n戦意を奪うオウムガイ型の魔物。", 206100);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ギャングオッター", 650, 0, 1, 206200, 3.5, 0, 1, 0, 270, "【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。", 206200);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ノーティビーバー", 650, 0, 1, 206201, 0.5, 0, 1, 0, 270, "【物理】中距離攻撃\n片っ端から木を削り倒すビーバー。\n削った木片は家作りだけでなく、\n家を守るための飛び道具となる。", 206201);
