INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "コマンド・ウキキー", 100, 0, 0, 206500, 2.5, 0, 1, 0, 300, "【物理】近接攻撃\nウキキーがより成長した姿。幼少より大岩を投げ\n続けたその太腕は、絶大な打撃を繰り出す。", 206500);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ヒヨリのシャドウ", 200, 0, 1, 206700, 2.295, 0, 1, 0, 270, "ヒヨリの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理大ダメージを与え、\n　その周りの狭い範囲の敵すべてにも\n　物理中ダメージを与える。\n・目の前の敵１キャラに物理中ダメージを\n　与える。\n・目の前の敵１キャラに物理小ダメージを与え、\n　さらに自分の物理攻撃力を中アップさせる。", 206700);
