INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ムイミのシャドウ", 240, 0, 2, 214901, 1.8, 0, 1, 0, 450, "ムイミの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理特大ダメージを与え\n　る。さらにＴＰが無くなるまでの間天楼覇断剣\n　を装備する。\n・前方範囲内の敵すべてに物理小ダメージを与え、\n　さらにスタンさせて小さく吹き飛ばす。\n・前方範囲内の敵すべての物理防御力を小ダウン\n　させる。", 214901);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "カヤのシャドウ", 320, 0, 1, 215001, 2.17, 0, 1, 0, 450, "カヤの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理特大ダメージを与え\n　る。バトル開始後、初弾のユニオンバーストは\n　ダメージ量が大幅アップ。\n・目の前の敵１キャラに物理中ダメージを与える。\n・自分の物理攻撃力と物理クリティカルを大アッ\n　プさせる。", 215001);
