INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "マインリザード", 335, 0, 14, 250200, 3.0, 0, 1, 0, 270, "【物理】\n堅牢な鉱石の表皮を持つ岩トカゲ。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 250200);
