INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "シードレイク", 1000, 0, 14, 301003, 1.8, 0, 1, 0, 270, "【物理】強力な氷のブレスで前衛を一瞬で\n氷漬けにし、咬みついて身体を砕く獰猛な深海竜。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から敵３体に物理ダメージ（召喚体無視）\n・前から敵３体に物理／魔法防御力ダウン（召喚\n　体無視・必中）\n【スキル１】\n・前から敵２体に魔法ダメージ（召喚体無視）\n・前から敵２体に凍結（召喚体無視・必中）\n・対象のＨＰ70％以下：凍結時間アップ\n【通常攻撃】\n・前から敵１体に物理ダメージ", 301003);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サベージベア", 1000, 0, 13, 301100, 1.7, 0, 1, 0, 270, "【物理】体格にそぐわぬ俊敏さと\n咬みつき攻撃で体力を奪い、\n巨大なツメで敵を引き裂く\n残虐非道な森の支配者。\n\n・前方4キャラに物理大ダメージを\n　与える。\n\n・前方1キャラに物理小ダメージを\n　与え、物理攻撃力を下げる。", 301100);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "マッドベア", 1000, 0, 13, 301101, 2.0, 0, 1, 0, 270, "【物理】特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・マッドベアから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に物理特大ダメージを与え、さ\n　らにスタン状態を付与する。このスキルの状態\n　異常はダメージを無効化しても付与される。\n・最も近い敵１キャラを中心にした小範囲に物理\n　小ダメージを与え、物理防御力を特大ダウンさ\n　せる。このスキルの状態異常はダメージを無効\n　化しても付与される。", 301101);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "マッドベア", 1000, 0, 13, 301102, 2.0, 0, 1, 0, 270, "【物理】特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＴＰ上昇を小ダウンさ\n　せるフィールドを展開する。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・マッドベアから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・敵から受けるすべてのダメージで発生する、\n　マッドベアのＴＰ回復量が大ダウンする。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に物理特大ダメージを与え、さ\n　らにノックバックさせ、スタン状態を付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・最も近い敵１キャラを中心にした小範囲に物理\n　小ダメージを与え、物理防御力を特大ダウンさ\n　せる。このスキルの状態異常はダメージを無効\n　化しても付与される。", 301102);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "マッドベア", 1000, 0, 13, 301103, 2.0, 0, 1, 0, 270, "【物理】特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・マッドベアから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を大ダウン\n　させる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に物理特大ダメージを与え、さ\n　らにスタンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・最も近い敵１キャラを中心にした小範囲に物理\n　大ダメージを与え、物理防御力を大ダウンさせ\n　る。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。", 301103);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ルナティックベア", 1000, 0, 13, 301104, 2.0, 0, 1, 0, 270, "【物理】塔の魔力で狂暴化した森の支配者。\n狂気に飲まれ放つ斬撃は、周囲の全てを粉砕する。\n\n・バトル開始時、自身にダメージ上限スキルを使\n　用し、一度の行動で受けるダメージの値が一定\n　を超えた場合、ダメージ量を減少させる。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン50%カット\n　効果を付与する。\n・ルナティックベアから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を小\n　ダウンさせる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に物理特大ダメージを与え、さ\n　らにスタンさせる。このスキルの状態異常はダ\n　メージを無効化しても付与される。\n・最も近い敵１キャラを中心にした小範囲に物理\n　大ダメージを与え、物理防御力を特大ダウンさ\n　せる。このスキルの状態異常はダメージを無効\n　化しても付与される。", 301104);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "マッドベア", 1000, 0, 13, 301105, 2.0, 0, 1, 0, 270, "【物理】特異な力で変質した\nサベージベア。怒りを込めた\n剛腕は、敵ごと地面を引き裂\nく。\n\n・ユニオンバーストは、最も\n　近い敵１キャラを中心にし\n　た小範囲に物理特大ダメー\n　ジを与え、さらにスタンさ\n　せる。\n\n・最も近い敵１キャラを中心\n　にした小範囲に物理大ダ\n　メージを与え、物理防御力\n　を大ダウンさせる。", 301105);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "マッドベア", 3000, 0, 13, 301106, 2.0, 0, 1, 0, 270, "【物理】特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・前から１番目の敵と周囲にスタン（必中）\n【スキル１】\n・前から１番目の敵と周囲に物理ダメージ\n・前から１番目の敵と周囲に物理防御力ダウン\n　（必中）\n【通常攻撃】\n・前から敵１体に物理ダメージ", 301106);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グレーターゴーレム", 1000, 0, 13, 301200, 1.6, 0, 1, 0, 270, "【物理】際限なくゴーレムを召喚し\n岩の針で体力と精神を同時に削る。\n無数のゴーレムの残骸を\n魔力で再構築した巨大なゴーレム。\n\n・前方4キャラに物理中ダメージを\n　与え、ＴＰを下げる。\n\n・ゴーレムを召喚する。", 301200);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "クリスタルゴーレム", 1000, 0, 13, 301201, 1.3, 0, 1, 0, 270, "【物理】ルナの魔力の結晶でその身を覆う巨大\nゴーレム。身に纏う結晶は堅牢な盾であり、全\nてを穿つ矛となる。\n\n・クリスタルゴーレムが毒、猛毒、呪い、火傷の\n　効果を受けると、行動速度が大ダウンする。\n・ユニオンバーストは、自身のデバフ効果をすべ\n　て解除し、物理防御力と魔法防御力を大アップ\n　させる。さらに、自分以外の味方の物理防御力\n　と魔法防御力を中アップさせる。\n・敵全体に物理中ダメージを与え、スタンさせる。\n・前方範囲内の敵に物理大ダメージを与え、味方\n　全体の物理攻撃力と魔法攻撃力を小アップさせ\n　る。さらに、自身のＴＰを中回復する。\n・自身のＨＰが40％を切ると、さらに攻撃が激\n　しく凶暴になる。", 301201);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "アイスゴーレム", 1000, 0, 13, 301202, 1.3, 0, 1, 0, 270, "【物理】凍てつく大地の洞窟に\n潜む巨大ゴーレム。身に纏う\n結晶は堅牢な盾にも矛にもなる。\n\n・アイスゴーレムが毒、猛毒、呪\n　い、火傷の効果を受けると、行\n　動速度が大ダウンする。\n\n・自身のデバフ効果をすべて解除\n　し、物理防御力と魔法防御力を\n　中アップする。\n\n・敵全体に物理中ダメージを与え\n　る。\n\n・前方範囲内の敵に物理大ダメー\n　ジを与える。", 301202);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "アイスゴーレム", 1000, 0, 13, 301202, 1.3, 0, 1, 0, 270, "【物理】凍てつく大地の洞窟に\n潜む巨大ゴーレム。身に纏う\n結晶は堅牢な盾にも矛にもなる。\n\n・アイスゴーレムが毒、猛毒、呪\n　い、火傷の効果を受けると、行\n　動速度が大ダウンする。\n\n・自身のデバフ効果をすべて解除\n　し、物理防御力と魔法防御力を\n　中アップする。\n\n・敵全体に物理中ダメージを与え\n　る。\n\n・前方範囲内の敵に物理大ダメー\n　ジを与える。", 301203);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グレーターゴーレム", 1000, 0, 13, 301204, 1.6, 0, 1, 0, 270, "【物理】無数の残骸で再構築された巨大ゴーレム。\n巨岩の腕を振り下ろし、地盤ごと敵を粉砕する。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に物理ダメージ\n【通常攻撃】\n・前から敵２体に物理ダメージ", 301204);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グレーターゴーレム", 1000, 0, 13, 301205, 1.6, 0, 1, 0, 270, "【物理】無数の残骸で再構築された巨大ゴーレム。\n巨岩の腕を振り下ろし、地盤ごと敵を粉砕する。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体に物理ダメージ\n・自身がバリア状態：ダメージアップ\n【スキル１】\n・自身に物理無効バリア\n・自身に魔法無効バリア\n【通常攻撃】\n・前から敵２体に物理ダメージ", 301205);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グレーターゴーレム", 1000, 0, 13, 301206, 1.6, 0, 1, 0, 270, "【物理】無数の残骸で再構築された巨大ゴーレム。\n巨岩の腕を振り下ろし、地盤ごと敵を粉砕する。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・敵全体に物理ダメージ\n・自身がバリア状態：ダメージアップ\n【スキル１】\n・自身に物理魔法無効バリア\n・自身に全ダメージカット\n【通常攻撃】\n・前から敵２体に物理ダメージ", 301206);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グラットン", 1000, 0, 4, 301300, 3.0, 0, 1, 0, 270, "暴食の凶獣として悪名轟く、天秤座の魔星獣。\n奪った魂の善悪をはかり、味の違いを愉しむ。\n\n・敵からの攻撃による麻痺、スタン、束縛、\n　行動速度低下効果への耐性を持たない。\n・敵全体に魔法大ダメージを与え、さらに自\n　身の物理攻撃力と魔法攻撃力を特大アップ。\n・後方範囲内の敵に魔法中ダメージ。\n・前方範囲内の敵に魔法中ダメージを与え、Ｔ\n　Ｐを小ダウン。さらに自身のＴＰを中アップ\n　させる。", 301300);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グラットン", 1000, 0, 4, 301301, 3.0, 0, 1, 0, 270, "【物理】あらゆる魂を貪る、天秤座の魔星獣。\nその天秤は、厳正とは程遠く暴食獣の食欲に傾く。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・自身の物理攻撃力が高いほど：ダメージアップ\n・敵全体に物理／魔法攻撃力ダウン（必中）\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n・自身に物理／魔法攻撃力アップ\n【スキル１】\n・後ろから敵２体に物理ダメージ\n【スキル２】\n・敵全体に魔法ダメージ\n・敵全体にＴＰダウン（必中）\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n・自身にＴＰ回復\n・自身の魔法攻撃力が高いほど：回復量アップ\n【ターゲットブレイク時発動】\n・部位１ブレイク：物理攻撃力ダウン\n・部位２ブレイク：物理／魔法防御力ダウン\n・部位３ブレイク：魔法攻撃力ダウン", 301301);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グラットン", 1000, 0, 4, 301302, 3.0, 0, 1, 0, 270, "【物理】あらゆる魂を貪る、天秤座の魔星獣。\nその天秤は、厳正とは程遠く暴食獣の食欲に傾く。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・自身の物理攻撃力が高いほど：ダメージアップ\n・敵全体に物理／魔法攻撃力ダウン（必中）\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n・自身に物理／魔法攻撃力アップ\n【スキル１】\n・後ろから敵２体に物理ダメージ\n【スキル２】\n・敵全体に魔法ダメージ\n・敵全体にＴＰダウン（必中）\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n・自身にＴＰ回復\n・自身の魔法攻撃力が高いほど：回復量アップ\n【スキル３】\n・前方範囲に束縛\n・前方範囲に行動速度ダウン\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n【ターゲットブレイク時発動】\n・部位１ブレイク：物理攻撃力ダウン\n・部位２ブレイク：物理／魔法防御力ダウン\n・部位３ブレイク：魔法攻撃力ダウン", 301302);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "グラットン", 1000, 0, 4, 301303, 3.0, 0, 1, 0, 270, "【物理】あらゆる魂を貪る、天秤座の魔星獣。\nその天秤は、厳正とは程遠く暴食獣の食欲に傾く。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・自身の物理攻撃力が高いほど：ダメージアップ\n・敵全体に物理／魔法攻撃力ダウン（必中）\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n・自身に物理／魔法攻撃力アップ\n【スキル１】\n・後ろから敵２体に物理ダメージ\n【スキル２】\n・敵全体に魔法ダメージ\n・敵全体にＴＰダウン（必中）\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n・自身にＴＰ回復\n・自身の魔法攻撃力が高いほど：回復量アップ\n【スキル３】\n・前方範囲に束縛\n・前方範囲に重複可能の行動速度ダウン\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n【スキル４】\n・敵全体に継続物理割合ダメージ\n・自身の物理攻撃力が高いほど：ダメージアップ\n・敵全体に継続ＴＰダウン（必中）\n・自身の魔法攻撃力が高いほど：ダウン量上昇\n・自身に調和状態\n【ターゲットブレイク時発動】\n・部位１ブレイク：物理攻撃力ダウン\n・部位２ブレイク：物理／魔法防御力ダウン\n・部位３ブレイク：魔法攻撃力ダウン", 301303);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "メデューサ", 1000, 0, 3, 301400, 2.5, 0, 1, 0, 270, "無数の毒蛇から成る頭髪の、乙女座の魔星獣。\n悲恋の苦しみは、全てを拒み石に変える。\n\n・敵全体に魔法大ダメージを与え、前方範囲内\n　の敵はさらに石化状態にする。\n・前方範囲内の敵に物理ダメージを与え、石化\n　状態の敵はさらにＴＰを大ダウン。\n・物理攻撃力が最も高い敵1キャラに魔法大ダ\n　メージを与え、毒状態にする。", 301400);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "メデューサ", 1000, 0, 3, 301401, 2.5, 0, 1, 0, 270, "【物理】果てのない愛憎に狂う乙女座の魔星獣。\n凄まじい嫉妬と拒絶が見るもの全てを石化させる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に魔法ダメージ\n・敵全体に石化（必中）\n・対象のＨＰ60％未満：石化時間アップ\n【スキル１】\n・敵全体に物理ダメージ\n・敵全体にＨＰ回復量ダウン（必中）\n・対象のＨＰ60％未満：対象に最大ＨＰダウン\n　（必中）\n【スキル２】\n・後ろから２番目の敵と周囲に魔法ダメージ\n・後ろから２番目の敵と周囲に毒（必中）\n・対象のＨＰ60％未満：毒ではなく猛毒（必中）", 301401);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "メデューサ", 1000, 0, 3, 301402, 2.5, 0, 1, 0, 270, "【物理】果てのない愛憎に狂う乙女座の魔星獣。\n凄まじい嫉妬と拒絶が見るもの全てを石化させる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に魔法ダメージ\n・敵全体に石化（必中）\n・対象のＨＰ60％未満：石化時間アップ\n【スキル１】\n・敵全体に物理ダメージ\n・敵全体にＨＰ回復量ダウン（必中）\n・対象のＨＰ60％未満：対象に最大ＨＰダウン\n　（必中）\n【スキル２】\n・後ろから２番目の敵と周囲に魔法ダメージ\n・後ろから２番目の敵と周囲に毒（必中）\n・対象のＨＰ60％未満：毒ではなく猛毒（必中）\n【スキル３】\n・後ろから２番目の敵と周囲に魔法ダメージ\n・対象のＨＰ60％未満：対象に石化（必中）", 301402);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "メデューサ", 1000, 0, 3, 301403, 2.5, 0, 1, 0, 270, "【物理】果てのない愛憎に狂う乙女座の魔星獣。\n凄まじい嫉妬と拒絶が見るもの全てを石化させる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウンカット\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体に魔法ダメージ\n・敵全体に石化（必中）\n・対象のＨＰ70％未満：石化時間アップ\n【スキル１】\n・敵全体に物理ダメージ\n・敵全体にＨＰ回復量ダウン（必中）\n・対象のＨＰ80％未満：対象に最大ＨＰダウン\n　（必中）\n【スキル２】\n・後ろから２番目の敵と周囲に魔法ダメージ\n・後ろから２番目の敵と周囲に毒（必中）\n・対象のＨＰ70％未満：毒ではなく猛毒（必中）\n【スキル３】\n・後ろから２番目の敵と周囲に魔法ダメージ\n・対象のＨＰ70％未満：対象に石化（必中）\n【スキル４】\n・しもべ２体召喚\n【召喚体特殊効果】\n・自身のＨＰが１％になったとき：メデューサに\n　物理固定ダメージ\n【召喚体スキル１】\n・前から敵１体に物理ダメージ\n・前から敵１体に毒（必中）", 301403);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "メデューサ", 1000, 0, 3, 301404, 2.5, 0, 1, 0, 270, "【物理】果てのない愛憎に狂う乙女座の魔星獣。\n凄まじい嫉妬と拒絶が見るもの全てを石化させる。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・バトル開始時、敵全体のＴＰ上昇を小ダウンさ\n　せるフィールドを展開する。\n・メデューサから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を小ダウン\n　させる。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、短時間石化状態にする。\n　キャラのＨＰが70%未満の場合、長時間石化\n　状態にする。\n　このスキルによるダメージでは、キャラのＴＰ\n　が回復しない。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前方範囲内の敵に物理大ダメージを与え、行動\n　不能状態の敵はさらにＴＰを中ダウン。\n　このスキルはダメージを無効化してもＴＰをダ\n　ウンさせる。\n・後ろから２番目の敵を中心にした小範囲に魔法\n　小ダメージを与え、毒状態を付与する。\n　このスキルにより付与された毒のダメージでは、\n　ＴＰが回復しない。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・後ろから２番目の敵を中心にした小範囲に魔法\n　小ダメージを与える。\n　キャラのＨＰが60％未満の場合、石化状態を\n　付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・しもべを２体召喚し、自身の物理防御力と魔法\n　防御力を大ダウン。\n・しもべは、最も近い１キャラに物理中ダメージ。\n　このスキルによるダメージでは、ＴＰ回復量は\n　減少する。\n・しもべのＨＰが一定値以下になった時、メデュ\n　ーサに物理固定ダメージを与える。", 301404);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ウールヴヘジン", 1000, 0, 12, 301500, 1.8, 0, 1, 0, 270, "【物理】遠吠えで獲物を麻痺させ\nひるんだ所をツメで引き裂く。\n数々の冒険者の行く手を阻んできた\n残虐を極める魔界の狼。\n\n・ランダムな2キャラに物理\n　大ダメージを与える。\n\n・前方3キャラに魔法小ダメージを\n　与え、麻痺状態にする。", 301500);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ウールヴヘジン", 3000, 0, 12, 301501, 1.8, 0, 1, 0, 270, "【物理】遠吠えで獲物をひるませツメで引き裂く。\n自身の欲望のまま、残虐の限りを尽くす魔狼。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・ランダムな敵３体にステータスアップ解除\n・このスキルの対象に物理ダメージ\n【スキル１】\n・敵全体に物理／魔法攻撃力ダウン\n・自身に行動速度アップ", 301501);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ウールヴヘジン", 3000, 0, 12, 301502, 1.8, 0, 1, 0, 270, "【物理】遠吠えで獲物をひるませツメで引き裂く。\n自身の欲望のまま、残虐の限りを尽くす魔狼。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効 \n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・ランダムな敵３体にステータスアップ解除\n・このスキルの対象に物理ダメージ\n【スキル１】\n・敵全体に物理／魔法攻撃力ダウン\n・自身に行動速度アップ", 301502);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ウールヴヘジン", 3000, 0, 12, 301503, 1.8, 0, 1, 0, 270, "【物理】遠吠えで獲物をひるませツメで引き裂く。\n自身の欲望のまま、残虐の限りを尽くす魔狼。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・ランダムな敵３体にステータスアップ解除\n・このスキルの対象に物理ダメージ\n【スキル１】\n・敵全体に物理／魔法攻撃力ダウン\n・自身に行動速度アップ\n【通常攻撃】\n・前から敵１体に物理ダメージ", 301503);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ウールヴヘジン", 3000, 0, 12, 301504, 1.8, 0, 1, 0, 270, "【物理】遠吠えで獲物をひるませ\nツメで引き裂く。自身の欲望の\nまま、残虐の限りを尽くす魔狼。\n\n・ユニオンバーストは、ランダムな\n　敵最大３キャラのステータスアッ\n　プ効果を解除した後、物理特大ダ\n　メージを与える。\n・敵全体の物理攻撃力と魔法攻撃力\n　を大ダウンさせ、自身の行動速度\n　を特大アップさせる。\n", 301504);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ジズ", 1000, 0, 3, 301600, 1.8, 0, 1, 0, 270, "【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。", 301600);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ジズ", 1000, 0, 3, 301600, 1.8, 0, 1, 0, 270, "【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。\n・前方範囲内の敵にキャラの最大ＨＰに応じた\n　中ダメージを与え、物理防御力を下げる。\n・ジズ自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。", 301601);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ストーム・ジズ", 1000, 0, 3, 301602, 1.8, 0, 1, 0, 270, "【物理】砂島にのさばる獰猛な大\n怪鳥。紫電を纏い嵐を巻き起こす\nさまは、天災と恐れられる。\n\n・ユニオンバーストは、敵全体に\n　最大ＨＰに応じた物理割合\n　ダメージを与える。\n\n・前から１番目の敵を中心にした\n　小範囲の最大２キャラに物理中\n　ダメージを与え、さらにスタン\n　させる。", 301602);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ジャバウォック", 1000, 0, 3, 301700, 2.6, 0, 1, 0, 270, "【物理】毒瘴の闇稜の頂に棲まう、醜悪なる狂獣。\n猛毒の瘴気で生を奪い、屍を食み力の糧にする。\n\n・ユニオンバーストは敵全体に魔法大ダメージと\n　毒状態を与え、さらにノックバックさせる。\n・前方2キャラに物理ダメージを与え、さらに物\n　理攻撃力とTPをダウンさせる。\n・ジャバウォック自身の残りHPが40%を切る\n　とさらに攻撃が激しく強暴になり、さらに3体\n　のモンスターを呼び寄せる。\n・残り時間が20秒を切ると、敵全体に強力な毒\n　状態を付与し、自身のTPを特大ダウン。", 301700);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ジャバウォック", 1000, 0, 3, 301701, 2.6, 0, 1, 0, 270, "【物理】毒瘴の闇稜の頂に棲まう、醜悪なる狂獣。\n猛毒の瘴気で生を奪い、屍を食み力の糧にする。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・敵全体に魔法ダメージ\n・敵全体に毒（必中）\n・敵全体をノックバック（必中）\n・敵全体にＨＰ吸収ダウン（必中）\n【スキル１】\n・前から敵２体に物理ダメージ\n・前から敵２体に物理攻撃力ダウン（必中）\n・前から敵２体にＴＰダウン（必中）\n【スキル２】\n・敵全体に猛毒\n・敵全体をノックバック\n・敵全体に継続ダメージアップ\n・自身にＴＰダウン\n【ＨＰ40％未満時発動】\n・自身に物理攻撃力アップ\n・自身に行動速度アップ\n・行動パターン変化", 301701);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サジタリウス", 1000, 0, 3, 301800, 2.0, 0, 1, 0, 270, "天を駆け数多の標的を射止める射手座の魔星獣。\n雷光纏いし矢は、刹那にすべてを穿つ至高の技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与える。\n　暗闇状態の敵はダメージが3倍になる。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵1キャラに強力な暗\n　闇状態を付与。\n・前方の敵3キャラに物理ダメージを与える。", 301800);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サジタリウス", 1000, 0, 3, 301801, 2.0, 0, 1, 0, 270, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体に魔法中ダメージを与え、さらに【黒星】\n　の数に応じて、追加の魔法割合ダメージを与え\n　る。このスキルによるダメージでは、ＴＰが回\n　復しない。\n・最も近い敵１キャラを中心にした中範囲内の飛\n　行状態のキャラを含む敵すべてに魔法大ダメー\n　ジを与える。さらに、範囲内の敵の数に応じて\n　自身の矢に【黒星】を追加する。このスキルに\n　よるダメージでは、ＴＰが回復しない。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を大ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。", 301801);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サジタリウス", 1000, 0, 3, 301802, 2.0, 0, 1, 0, 270, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体に魔法中ダメージを与え、さらに【黒星】\n　の数に応じて、追加の魔法割合ダメージを与え\n　る。このスキルによるダメージでは、ＴＰが回\n　復しない。\n・最も近い敵１キャラを中心にした中範囲内の飛\n　行状態のキャラを含む敵すべてに魔法大ダメー\n　ジを与える。さらに、範囲内の敵の数に応じて\n　自身の矢に【黒星】を追加する。このスキルに\n　よるダメージでは、ＴＰが回復しない。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を大ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。\n・飛行状態のキャラを含む敵全体のバリアを解除\n　し、魔法小ダメージを与え、さらに前方中範囲\n　内の飛行状態のキャラを含む敵すべてに魔法大\n　ダメージを与える。このスキルによるダメージ\n　では、ＴＰが回復しない。", 301802);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サジタリウス", 1000, 0, 3, 301803, 2.0, 0, 1, 0, 270, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を中ダウンさせる。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を特大ダ\n　ウンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体に魔法中ダメージを与え、さらに【黒星】\n　の数に応じて、追加の魔法割合ダメージを与え\n　る。このスキルによるダメージでは、ＴＰが回\n　復しない。\n・最も近い敵１キャラを中心にした中範囲内の飛\n　行状態のキャラを含む敵すべてに魔法大ダメー\n　ジを与える。さらに、範囲内の敵の数に応じて\n　自身の矢に【黒星】を追加する。このスキルに\n　よるダメージでは、ＴＰが回復しない。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を大ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。\n・飛行状態のキャラを含む敵全体のバリアを解除\n　し、魔法小ダメージを与え、さらに前方中範囲\n　内の飛行状態のキャラを含む敵すべてに魔法大\n　ダメージを与える。その後【黒星】の数に応じ\n　て、自身のＴＰを回復する。このスキルによる\n　ダメージでは、ＴＰが回復しない。", 301803);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ウェアオルカ", 1000, 0, 11, 301900, 1.7, 0, 1, 0, 270, "【物理】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。\n\n・前方4キャラに物理中ダメージを\n　与え、物理攻撃力を下げる。\n\n・前方2キャラに魔法小ダメージを\n　与え、ＴＰを下げる。", 301900);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "キリングオルカ", 1000, 0, 11, 301901, 1.5, 0, 1, 0, 270, "【物理】ルナの魔力により凶悪化した大海の狩人。\n鍛え抜いた躰で、刃と化した水流の一撃を放つ。\n\n・前方４キャラに物理大ダメージを与え、\n　物理攻撃力を中ダウンさせる。さらに、\n　味方全員のＴＰ上昇を小アップする。\n・前方小範囲に魔法中ダメージを与え、\n　ＴＰを大ダウン。\n・後ろから２番目のキャラを中心にした\n　小範囲に物理中ダメージを与え、\n　魔法攻撃力を大ダウンさせる。", 301901);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ワイルドグリフォン", 1000, 0, 11, 302000, 1.6, 0, 1, 0, 270, "【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n【特殊効果】\n・飛行効果無視\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・前から敵５体に魔法ダメージ\n・前から敵５体をノックバック（必中）\n・前から敵５体にスタン（必中）", 302000);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ワイルドグリフォン", 1000, 0, 11, 302001, 1.6, 0, 1, 0, 270, "過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。", 302001);
