INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "バーンサウルス", 1000, 0, 13, 303608, 1.7, 0, 1, 0, 270, "【物理】強靭な脚と鋭利なツメを操る獰猛な恐竜。\n滅びた大地でマグマを喰らい生きる孤高の存在。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ（ＴＰ\n　回復不可）\n・対象数が少ないほど：ダメージアップ\n・自身に行動速度アップ\n【スキル１】\n・前から１番目の敵と周囲に物理ダメージ\n・前から１番目の敵と周囲に物理防御力ダウン\n　（必中）\n・対象数が少ないほど：ダウン量アップ\n【通常攻撃】\n・前から敵１体に物理ダメージ", 303608);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "バーンサウルス", 1000, 0, 13, 303609, 1.7, 0, 1, 0, 270, "【物理】強靭な脚と鋭利なツメを操る獰猛な恐竜。\n滅びた大地でマグマを喰らい生きる孤高の存在。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・対象数が少ないほど：ダメージアップ\n・自身に行動速度アップ\n【スキル１】\n・前から１番目の敵と周囲に物理ダメージ\n・前から１番目の敵と周囲に物理防御力ダウン\n　（必中）\n・対象数が少ないほど：ダウン量アップ", 303609);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ヘルハウンド", 1000, 0, 14, 303700, 0.7, 0, 1, 0, 270, "【物理】火山に身を落としてなお\n地獄の炎を纏い蘇った双頭の魔犬。\n愛犬ボニーと共に縄張りを駆け、\n侵入者には灼熱の洗礼を浴びせる。\n\n・敵全体に物理中ダメージを与え、\n　火傷状態にする。さらに、召喚\n　モンスターのＨＰを回復する。\n\n・ボニーを召喚する。", 303700);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "バーンウルフ", 1000, 0, 14, 303701, 0.7, 0, 1, 0, 270, "【物理】塔の魔力で、魔界の炎を顕現させる魔狼。\n灼熱の地で、相棒のボニーとともに待ち受ける。\n\n・敵全体に物理大ダメージを与え火傷状態にする。\n　さらに、ボニーのHPを回復する。\n・ボニーを召喚する。\n・敵全体に物理中ダメージを与え、行動速度をダ\n　ウンさせる。さらに、味方全体の物理攻撃力を\n　大アップさせる。", 303701);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "アルマ", 1000, 0, 16, 303800, 2.0, 0, 1, 0, 270, "【物理】はるか古より迷宮を守護する防衛兵器。すべての罠を超えた最奥で侵入者を静かに待つ。\n・敵全体にキャラの最大ＨＰに応じた\n　魔法中ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与える。", 303800);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "アルマ", 1000, 0, 16, 303800, 2.0, 0, 1, 0, 270, "【物理】はるか古より迷宮を守護する防衛兵器。すべての罠を超えた最奥で侵入者を静かに待つ。\n・敵全体にキャラの最大ＨＰに応じた\n　魔法特大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与える。\n・残りＨＰの高い敵に対して最大ＨＰに応じた\n　魔法中ダメージを与えさらにスタンさせる。\n・アルマ自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。", 303801);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サイクロプス", 1000, 0, 13, 303900, 2.0, 0, 1, 0, 270, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サイクロプスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体にキャラの最大ＨＰに応じた魔法割合ダ\n　メージを与え、ＴＰを小ダウンさせ、ステータ\n　スアップ効果を解除する。さらに、バトル終了\n　まで物理攻撃力を大ダウン、魔法攻撃力を中ダ\n　ウンさせる。このスキルの状態異常はダメージ\n　を無効化しても付与される。このスキルはダ\n　メージを無効化してもＴＰをダウンさせる。\n・物理攻撃力もしくは魔法攻撃力が最も高い敵１\n　キャラを中心にした小範囲に物理特大ダメージ\n　を与え、スタン状態を付与する。このスキルに\n　よるダメージでは、ＴＰが回復しない。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。", 303900);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サイクロプス", 1000, 0, 13, 303901, 2.0, 0, 1, 0, 270, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・敵全体に魔法大ダメージ。\n　残りＨＰが１００％のキャラにはダメージが\n　アップし、さらに行動速度をダウンさせる。\n・最も物理攻撃力が高い１キャラに、最大ＨＰ\n　に応じた物理中ダメージを与え、ＴＰダウン。", 303901);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サイクロプス", 1000, 0, 13, 303902, 2.0, 0, 1, 0, 270, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。 \n・バトル開始時、敵全体のＴＰ上昇を小ダウンさ\n　せるフィールドを展開する。\n・バトル開始時、自身にＴＰダウン25％カット\n　効果を付与する。\n・サイクロプスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体にキャラの最大ＨＰに応じた魔法割合ダ\n　メージを与え、ＴＰを中ダウンさせ、ステータ\n　スアップ効果を解除する。さらに、バトル終了\n　まで物理攻撃力を大ダウン、魔法攻撃力を中ダ\n　ウンさせる。このスキルの状態異常はダメージ\n　を無効化しても付与される。このスキルはダ\n　メージを無効化してもＴＰをダウンさせる。\n・物理攻撃力もしくは魔法攻撃力が最も高い敵１\n　キャラを中心にした小範囲に物理特大ダメージ\n　を与え、スタン状態を付与する。このスキルに\n　よるダメージでは、ＴＰが回復しない。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・通常攻撃によるダメージでは、ＴＰ回復量が特\n　大ダウンする。", 303902);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ヒトツメ巨人", 1000, 0, 13, 303903, 2.0, 0, 1, 0, 270, "【物理】砂塵の中、山のように佇\nむ一つ目の巨人。単眼から超強力\nな光線を発し冒険者を薙ぎ払う。\n\n・ユニオンバーストは、敵全体に\n　魔法大ダメージを与え、さらに\n　行動速度を中ダウンさせる。\n　残りＨＰが100％のキャラ\n　には、ダメージがアップする。\n\n・最も物理攻撃力が高い１キャラ\n　に、最大ＨＰに応じた物理割合\n　ダメージを与え、さらにＴＰを\n　大ダウンさせる。", 303903);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "サイクロプス", 3000, 0, 13, 303904, 2.0, 0, 1, 0, 270, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・飛行状態の敵を攻撃対象にできる。\n・サイクロプスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を特大ダ\n　ウンさせる。\n・サイクロプスから受けるすべての状態異常とス\n　テータスダウン効果は、ダメージを無効化して\n　も付与される。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与え、物理攻\n　撃力と魔法攻撃力とＨＰ回復量とＴＰを特大ダ\n　ウンさせる。このスキルによるダメージでは、\n　ＴＰが回復しない。\n・ランダムな敵キャラに、単体物理中ダメージを\n　計３回与え、スタン状態を計３回付与する。", 303904);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ムーバ", 1000, 0, 13, 304000, 2.0, 0, 1, 0, 270, "【物理】夢幻より生まれ、闇を彷徨う魔獣。\n悪夢の牢に獲物を縛り、旨そうに生気を喰う。\n\n・挑発効果の影響を受けやすい。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ムーバから受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、物理攻撃力もしくは魔法\n　攻撃力が最も高い敵１キャラに魔法特大ダメー\n　ジを与え、ＴＰを特大ダウンさせる。このスキ\n　ルはダメージを無効化してもＴＰをダウンさせ\n　る。\n・敵の挑発を無視して、最も遠い敵１キャラを中\n　心にした小範囲内の敵すべてに呪い状態を付与\n　する。このスキルにより付与された呪いのダ\n　メージでは、ＴＰが回復しない。", 304000);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ムーバ", 1000, 0, 13, 304001, 2.0, 0, 1, 0, 270, "【物理】夢幻より生まれ、闇を彷徨う魔獣。\n悪夢の牢に獲物を縛り、旨そうに生気を喰う。\n\n・挑発効果の影響を受けやすい。\n・バトル開始時、自身の物理防御力を大アップさ\n　せ、魔法防御力を中アップさせる。この防御力\n　アップ効果で上昇した防御力は、バトル中に受\n　ける防御力ダウンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。\n・バトル開始時、敵全体のＴＰ上昇を小ダウンさ\n　せるフィールドを展開する。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・ムーバから受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、物理攻撃力もしくは魔法\n　攻撃力が最も高い敵１キャラのステータスアッ\n　プ効果を解除した後、魔法特大ダメージを与え、\n　ＴＰを特大ダウンさせる。このスキルはダメー\n　ジを無効化してもＴＰをダウンさせる。\n・敵の挑発を無視して、最も遠い敵１キャラを中\n　心にした小範囲内の敵すべてのＴＰを小ダウン\n　させ、呪い状態を付与する。このスキルにより\n　付与された呪いのダメージでは、ＴＰが回復し\n　ない。", 304001);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ムーバ", 1000, 0, 13, 304002, 2.0, 0, 1, 0, 270, "【物理】夢幻より生まれ、闇を彷徨う魔獣。\n悪夢の牢に獲物を縛り、旨そうに生気を喰う。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・物理か魔法攻撃力が最も高い敵１体にステータ\n　スアップ解除\n・このスキルの対象に魔法ダメージ\n・このスキルの対象にＴＰダウン\n【スキル１】\n・後ろから１番目の敵と周囲にＴＰダウン（挑発\n　無視）\n・後ろから１番目の敵と周囲に呪い（挑発無視・\n　ＴＰ回復不可）\n【通常攻撃】\n・物理か魔法攻撃力が最も高い敵１体に物理ダ\n　メージ", 304002);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ムーバ", 1000, 0, 13, 304003, 2.0, 0, 1, 0, 270, "【物理】夢幻より生まれ、闇を彷徨う魔獣。\n悪夢の牢に獲物を縛り、旨そうに生気を喰う。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・物理か魔法攻撃力が最も高い敵１体にステータ\n　スアップ解除、魔法ダメージ\n・物理か魔法攻撃力が最も高い敵１体にＴＰダウ\n　ン、呪詛（必中）\n・呪詛状態の敵全体にステータスアップ解除、魔\n　法ダメージ（飛行効果無視）\n【スキル１】\n・後ろから１番目の敵と周囲にＴＰダウン、呪い\n　（挑発無視）\n【通常攻撃】\n・物理か魔法攻撃力が最も高い敵１体に物理ダ\n　メージ", 304003);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "破角の幻馬", 1000, 0, 13, 304004, 2.0, 0, 1, 0, 270, "【物理】ジオ・ニヴルヘルを縄張り\nとし、闇に潜み獲物を待ち伏せる。\n彷徨う者たちを夢幻に誘う。\n\n・バトル開始時、味方全体の物理防\n　御力と魔法防御力を小アップ。\n　（ダウン不可）\n・ユニオンバーストは、物理もしく\n　は魔法攻撃力が最も高い敵１キャ\n　ラのステータスアップ効果を解除\n　後、魔法特大ダメージを与え、\n　ＴＰを特大ダウン。ダメージを無\n　効化してもＴＰダウン。\n・敵の挑発を無視し、最も遠い敵１\n　キャラを中心にした中範囲内の敵\n　にＴＰ小ダウンと呪い状態を付\n　与。このスキルの呪いではＴＰが\n　回復しない。\n・通常攻撃は、物理もしくは魔法攻\n　撃力が最も高い敵１キャラに物理\n　特大ダメージを与える。", 304004);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "ティタノタートル", 1000, 0, 13, 304100, 2.5, 0, 1, 0, 270, "甲羅に大樹が根ざすほどの、老大な陸ガメ。\n敵の攻撃を耐え続け、憤怒の反撃を見舞う。\n\n・敵全体に物理大ダメージを与える。\n・甲羅にこもっている間に受けたダメージ量に\n　応じた物理ダメージを与える。", 304100);
