INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "邪餅神ガミィ", 3000, 0, 16, 306005, 1.6, 0, 1, 0, 270, "【MODE2】邪念の残滓が群集し邪神と化した。\n顕現した煩悩を全て浄化し、新年を迎えよう！\n\n・前方1キャラに物理ダメージを与える。\n・前方範囲内に魔法中ダメージを与え、\n　大きく吹き飛ばす。\n・6個の邪念を込めてボンノウ（赤）を召喚する。\n・3個の邪念を込めてボンノウ（紫）を召喚する。\n・1個の邪念を込めてボンノウ（青）を召喚する。", 306005);
