INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "邪餅神ガミィ", 2160, 0, 16, 306000, 1.6, 0, 1, 0, 270, "【物理】邪念の残滓が群集し邪神と化した。\n顕現した煩悩を全て浄化し、新年を迎えよう！\n\n・ユニオンバーストは敵全体に\n　キャラの最大ＨＰに応じた\n　物理大ダメージを与える。\n・前方３キャラに魔法中ダメージを与え、\n　物理攻撃力を小ダウンさせる。\n・時限式の爆弾モチを前方中距離に召喚する。\n・爆弾モチは、召喚されてから\n　８秒後に大爆発し、範囲内の敵すべてに\n　物理中ダメージを与える。\n　ただし、爆発するまでに爆弾モチ自身の\n　ＨＰが50％を切ると、\n　敵全体のＨＰを小回復し、消滅する。", 306007);
