INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, "巨鋼機獣・改造ゲドン", 3000, 0, 16, 323000, 1.6, 0, 1, 0, 270, "【物理】並行世界より連れ込まれた半機半生の怪\n獣。身に纏う鋼鉄の装甲はあらゆる攻撃を遮断し、\n放たれる灼熱の光線は万物を灰燼に帰す。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n【ユニオンバースト】\n・敵全体にバリア解除\n・敵全体に魔法割合ダメージ\n・敵全体にＴＰダウン\n・敵全体に行動速度ダウン\n・敵全体にＨＰ吸収ダウン（必中）\n・自身のＨＰ50％未満：ダメージと効果量アッ\n　プ\n【スキル１】\n・敵全体に物理ダメージ\n・対象のＨＰ70％未満：スタン（必中）\n【スキル２】\n・敵全体に物理ダメージ\n【スキル３】\n・自身のＨＰ50％未満：強化状態に変更\n【強化状態：スキル１】\n・敵全体に物理ダメージ\n・敵全体に魔法ダメージ\n・対象のＨＰ90％未満：スタン（必中）\n【強化状態：スキル２】\n・敵全体に物理ダメージ\n・敵全体に魔法ダメージ", 323000);
