INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("グレーターゴーレム", 301205, 1, 0, 0, 0, "【物理】無数の残骸で再構築された巨大ゴーレム。\n巨岩の腕を振り下ろし、地盤ごと敵を粉砕する。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体に物理ダメージ\n・自身がバリア状態：ダメージアップ\n【スキル１】\n・自身に物理無効バリア\n・自身に魔法無効バリア\n【通常攻撃】\n・前から敵２体に物理ダメージ", 301205, 270, 1.6, 13, 0, 1000);
