INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("イクト・ガラディオン", 302200, 2, 0, 0, 0, "【魔法】ルナの魔力で目覚めた、古代の機械兵器。\n無慈悲に放たれる魔力が、侵入者を阻む。\n\n・敵全体に魔法特大ダメージを与え、魔法防御力\n　を大ダウン。\n・前から３キャラに物理大ダメージを与える。\n・前から２キャラに魔法大ダメージを\n　与え、麻痺状態にする。", 302200, 270, 1.6, 16, 0, 1000);
