INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("アークガーディアン", 302400, 1, 0, 0, 0, "【物理】太古より遺跡を護り続ける人工生命体。\n創造主の秘術で、無限に動力を得て動き続ける。\n\n・ユニオンバーストは敵全体に魔法大ダメージを\n　与える。\n・前方範囲内に物理ダメージを与える。\n・敵全体に魔法ダメージを与え、さらに沈黙させ\n　る。", 302400, 270, 1.6, 16, 0, 1000);
