INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("マスター・ウキキー", 306300, 1, 0, 0, 0, "【物理】ルナの魔力を使いウキキーを統べる仙猿。\n宝武具の棍を自在に操り、すべての敵をなぎ払う。\n\n・敵全体に物理大ダメージを与える。\n　この攻撃は火傷状態の敵に対して、\n　ダメージが1.8倍になる。\n・前方中範囲の敵に物理中ダメージを与える。\n・前方小範囲の敵に魔法中ダメージを与え、\n　火傷状態にする。", 306300, 270, 1.8, 16, 0, 1000);
