INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("ペテルギウス", 307400, 1, 0, 0, 0, "", 307400, 270, 1.6, 16, 0, 3000);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("ペテルギウス", 307401, 1, 0, 0, 0, "【物理】ルグニカに悪名高き魔女教の幹部。\n異世界に行き着いてもなお自身の信念に従い、\n強力な権能『見えざる手』の力で暗躍する。\n\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身を中心とした範囲内の敵すべてに魔法中ダ\n　メージを与え、ＴＰを小ダウンさせる。\n　さらに敵全体に魔法中ダメージを与える。\n・最も遠い敵２キャラに、キャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・最も近い敵１キャラに物理大ダメージを与える。\n・向かって左側のターゲットのブレイク時、自身\n　の物理防御力と魔法防御力と物理攻撃力と魔法\n　攻撃力が小ダウンする。\n・中央のターゲットのブレイク時、自身の物理防\n　御力と魔法防御力と魔法攻撃力が中ダウン、自\n　身の物理攻撃力が小ダウンする。\n・向かって右側のターゲットのブレイク時、自身\n　の物理防御力と魔法防御力と魔法攻撃力が中ダ\n　ウン、自身の物理攻撃力が小ダウンする。", 307400, 270, 1.6, 16, 0, 3000);
