INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("ストーム・ジズ", 307506, 1, 0, 0, 0, "【物理】深域にのさばる獰猛な大怪鳥。紫電を\n纏い嵐を巻き起こすさまは、天災と恐れられる。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に魔法ダメージ（ＴＰ回復不可）\n・敵全体に魔法防御力ダウン（必中）\n【スキル１】\n・前から敵２体に物理ダメージ\n・前から敵２体にスタン（必中）\n【スキル２】\n・前から敵２体に物理ダメージ\n・前から敵２体に物理防御力ダウン（必中）\n【通常攻撃】\n・前から敵１体に物理ダメージ", 307506, 270, 1.8, 3, 0, 1000);
