INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("オオドクマンドラゴラ", 308201, 1, 0, 0, 0, "【物理】異常発達したアオイのマンドラゴラ。\n強靭な根と種子の弾丸で眼前の敵を圧倒する。\n\n・前方２キャラに最大ＨＰに応じた物理割合\n　ダメージ。\n・ユニオンバーストは、敵全体に\n　物理大ダメージを与え、与えた合計ダメー\n　ジに応じて自身のＨＰを回復させる。\n・敵全体に連続で物理小ダメージを与えつつ、\n　物理防御力を３度ダウンさせる。\n・自身の物理攻撃力をアップさせつつ、ＴＰ\n　を上昇させる。", 308200, 270, 1.6, 16, 0, 3000);
