INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("なかよしＸ", 310705, 1, 0, 0, 0, "【ＭＯＤＥ２】鋼鉄の球体が、完璧なちぇる語で\n囁きかける。「ちぇる～ん」。心を掴み離さない\n洗脳の言の葉は、確実に世界をちぇるへと誘う。\n\n・ランダムな１キャラに魔法中ダメージを与え、\n　沈黙と誘惑状態にする。", 310700, 270, 1.4, 16, 0, 3000);
