INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("アンチビースト・トリニティ", 314400, 2, 0, 0, 0, "", 314400, 270, 1.6, 16, 0, 3000);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("アンチビースト・トリニティ", 314401, 2, 0, 0, 0, "【魔法】魔法少女たちのパワーを吸い取って進化\nしたアンチビースト。ランドソルはこのまま魔の\n手に堕ちてしまうのか！？負けるなノワールズ！\n\n・ユニオンバーストは、自身に近い敵３キャラの\n　うち、ＨＰが80％未満のキャラを誘惑状態に\n　する。その後、自身に近い敵３キャラにキャラ\n　の最大ＨＰに応じた魔法割合ダメージを与える。\n　誘惑状態の敵にはダメージがアップする。\n・アンチビーストの下僕を召喚し敵全体をノック\n　バックさせる。アンチビーストの下僕は、一定\n　時間経過すると最も近い敵１キャラを誘惑状態\n　にしたあと、消滅する。ただしアンチビースト\n　の下僕のＨＰが一定値以下になった場合は、\n　パーティ全体の物理攻撃力と魔法攻撃力を特大\n　アップさせたあと、消滅する。\n・自身に近い敵２キャラに魔法小ダメージを与え\n　る。さらに、自身に物理無効バリアを展開する。\n・自身に近い敵２キャラにキャラの最大ＨＰに応\n　じた魔法割合ダメージを与える。", 314400, 270, 1.6, 16, 0, 3000);
