INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES ("海将タツノココマンダー", 316301, 1, 0, 0, 0, "【物理】強靭な肉体で三叉槍を操る海獣。人間に\n捕われた怒りで暴れ回り、ビーチを恐怖に染める。\n\n・ユニオンバーストは、前から３番目のキャラに\n　魔法大ダメージを与え、前から３番目のキャラ\n　を中心にした小範囲の敵を気絶させる。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・自身の魔法攻撃力を大アップさせ、さらにＴＰ\n　を中回復する。\n・前から３番目のキャラに物理中ダメージを与え\n　る。", 316300, 270, 1.6, 16, 0, 3000);
