INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (216400, 2, "プレシア（フォールン）のシャドウ", 44, 0, 0, 0, 0, "プレシア（フォールン）の姿をした特殊な魔物", 540, 450, 216400, 2.09);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (216501, 1, "リリ（フォールン）のシャドウ", 3, 0, 0, 0, 0, "リリ（フォールン）の姿をした特殊な魔物\n\n・【召喚】目の前の敵１キャラに物理大ダメージ\n　を与える。このスキルのダメージは自分のシャ\n　ドウの数に応じて増加する。さらにユニオン\n　バーストを使用しない、自分のシャドウを２体\n　生成する。\n　生成されたシャドウは自分を飛行状態にする。\n・自分を中心とした範囲内の味方すべての物理攻\n　撃力を特大アップさせる。\n・目の前の敵１キャラに物理大ダメージを与え、\n　物理防御力を小ダウンさせる。", 464, 450, 216501, 2.09);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (216601, 1, "クリア（フォールン）のシャドウ", 0, 0, 0, 0, 0, "クリア（フォールン）の姿をした特殊な魔物\n\n・目の前の敵１キャラに物理中ダメージを与え、\n　自分のＨＰを中回復させる。自分を除く味方の\n　残りＨＰに応じて、このスキルのダメージが\n　アップする。\n・目の前の敵１キャラに物理中ダメージを与え、\n　物理防御力を中ダウンさせる。自分を除く味方\n　の残りＨＰに応じて、このスキルのダメージが\n　アップする。\n・自分以外の最もＨＰの低い味方１キャラに継続\n　ＨＰ回復状態と【堕天使の加護】を付与する。\n　【堕天使の加護】が付与されている間、その\n　キャラへの敵の攻撃をすべて代わりに受け、自\n　分への物理ダメージと魔法ダメージをカットし、\n　自分が敵から受ける行動不能を無効化する。", 406, 450, 216601, 2.09);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (216700, 1, "シールド・レリック", 0, 0, 0, 0, 0, "【物理】ダメージカット\nシャドウと共に聖域を守護する、記憶の番人。\n真なる力を欲するものに、相応の試練を与える。\n\n・味方全体に物理魔法ダメージカット効果を付与\n　する。さらに、自身以外のダメージを受けてい\n　る味方に無敵状態を付与し、物理攻撃力と魔法\n　攻撃力をアップさせる。", 400, 270, 216700, 1.9);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (216800, 2, "アタック・レリック", 0, 0, 0, 0, 0, "【魔法】遠距離攻撃\nシャドウと共に聖域を守護する、記憶の番人。\n真なる力を欲するものに、相応の試練を与える。\n\n・敵の挑発を無視して、最も遠い敵１キャラに\n　５万の魔法固定ダメージを与える。このスキ\n　ルを使用するたび、次のスキルを使用するま\n　での時間が短くなる。", 1200, 270, 216800, 0.9);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (216800, 2, "アタック・レリック", 0, 0, 0, 0, 0, "【魔法】遠距離攻撃\nシャドウと共に聖域を守護する、記憶の番人。\n真なる力を欲するものに、相応の試練を与える。\n\n・敵の挑発を無視して、最も遠い敵１キャラに\n　１００万の魔法固定ダメージを与える。この\n　スキルを使用するたび、次のスキルを使用す\n　るまでの時間が短くなる。", 1200, 270, 216801, 0.9);
