INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301504, 1, "ウールヴヘジン", 12, 0, 0, 0, 0, "【物理】遠吠えで獲物をひるませ\nツメで引き裂く。自身の欲望の\nまま、残虐の限りを尽くす魔狼。\n\n・ユニオンバーストは、ランダムな\n　敵最大３キャラのステータスアッ\n　プ効果を解除した後、物理特大ダ\n　メージを与える。\n・敵全体の物理攻撃力と魔法攻撃力\n　を大ダウンさせ、自身の行動速度\n　を特大アップさせる。\n", 3000, 270, 301504, 1.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301600, 1, "ジズ", 3, 0, 0, 0, 0, "【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。", 1000, 270, 301600, 1.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301600, 1, "ジズ", 3, 0, 0, 0, 0, "【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。\n・前方範囲内の敵にキャラの最大ＨＰに応じた\n　中ダメージを与え、物理防御力を下げる。\n・ジズ自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。", 1000, 270, 301601, 1.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301602, 1, "ストーム・ジズ", 3, 0, 0, 0, 0, "【物理】砂島にのさばる獰猛な大\n怪鳥。紫電を纏い嵐を巻き起こす\nさまは、天災と恐れられる。\n\n・ユニオンバーストは、敵全体に\n　最大ＨＰに応じた物理割合\n　ダメージを与える。\n\n・前から１番目の敵を中心にした\n　小範囲の最大２キャラに物理中\n　ダメージを与え、さらにスタン\n　させる。", 1000, 270, 301602, 1.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301700, 1, "ジャバウォック", 3, 0, 0, 0, 0, "【物理】毒瘴の闇稜の頂に棲まう、醜悪なる狂獣。\n猛毒の瘴気で生を奪い、屍を食み力の糧にする。\n\n・ユニオンバーストは敵全体に魔法大ダメージと\n　毒状態を与え、さらにノックバックさせる。\n・前方2キャラに物理ダメージを与え、さらに物\n　理攻撃力とTPをダウンさせる。\n・ジャバウォック自身の残りHPが40%を切る\n　とさらに攻撃が激しく強暴になり、さらに3体\n　のモンスターを呼び寄せる。\n・残り時間が20秒を切ると、敵全体に強力な毒\n　状態を付与し、自身のTPを特大ダウン。", 1000, 270, 301700, 2.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301701, 1, "ジャバウォック", 3, 0, 0, 0, 0, "【物理】毒瘴の闇稜の頂に棲まう、醜悪なる狂獣。\n猛毒の瘴気で生を奪い、屍を食み力の糧にする。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・敵全体に魔法ダメージ\n・敵全体に毒（必中）\n・敵全体をノックバック（必中）\n・敵全体にＨＰ吸収ダウン（必中）\n【スキル１】\n・前から敵２体に物理ダメージ\n・前から敵２体に物理攻撃力ダウン（必中）\n・前から敵２体にＴＰダウン（必中）\n【スキル２】\n・敵全体に猛毒\n・敵全体をノックバック\n・敵全体に継続ダメージアップ\n・自身にＴＰダウン\n【ＨＰ40％未満時発動】\n・自身に物理攻撃力アップ\n・自身に行動速度アップ\n・行動パターン変化", 1000, 270, 301701, 2.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301800, 1, "サジタリウス", 3, 0, 0, 0, 0, "天を駆け数多の標的を射止める射手座の魔星獣。\n雷光纏いし矢は、刹那にすべてを穿つ至高の技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与える。\n　暗闇状態の敵はダメージが3倍になる。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵1キャラに強力な暗\n　闇状態を付与。\n・前方の敵3キャラに物理ダメージを与える。", 1000, 270, 301800, 2.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301801, 1, "サジタリウス", 3, 0, 0, 0, 0, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体に魔法中ダメージを与え、さらに【黒星】\n　の数に応じて、追加の魔法割合ダメージを与え\n　る。このスキルによるダメージでは、ＴＰが回\n　復しない。\n・最も近い敵１キャラを中心にした中範囲内の飛\n　行状態のキャラを含む敵すべてに魔法大ダメー\n　ジを与える。さらに、範囲内の敵の数に応じて\n　自身の矢に【黒星】を追加する。このスキルに\n　よるダメージでは、ＴＰが回復しない。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を大ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。", 1000, 270, 301801, 2.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301802, 1, "サジタリウス", 3, 0, 0, 0, 0, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体に魔法中ダメージを与え、さらに【黒星】\n　の数に応じて、追加の魔法割合ダメージを与え\n　る。このスキルによるダメージでは、ＴＰが回\n　復しない。\n・最も近い敵１キャラを中心にした中範囲内の飛\n　行状態のキャラを含む敵すべてに魔法大ダメー\n　ジを与える。さらに、範囲内の敵の数に応じて\n　自身の矢に【黒星】を追加する。このスキルに\n　よるダメージでは、ＴＰが回復しない。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を大ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。\n・飛行状態のキャラを含む敵全体のバリアを解除\n　し、魔法小ダメージを与え、さらに前方中範囲\n　内の飛行状態のキャラを含む敵すべてに魔法大\n　ダメージを与える。このスキルによるダメージ\n　では、ＴＰが回復しない。", 1000, 270, 301802, 2.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301803, 1, "サジタリウス", 3, 0, 0, 0, 0, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を中ダウンさせる。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を特大ダ\n　ウンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体に魔法中ダメージを与え、さらに【黒星】\n　の数に応じて、追加の魔法割合ダメージを与え\n　る。このスキルによるダメージでは、ＴＰが回\n　復しない。\n・最も近い敵１キャラを中心にした中範囲内の飛\n　行状態のキャラを含む敵すべてに魔法大ダメー\n　ジを与える。さらに、範囲内の敵の数に応じて\n　自身の矢に【黒星】を追加する。このスキルに\n　よるダメージでは、ＴＰが回復しない。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を大ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。\n・飛行状態のキャラを含む敵全体のバリアを解除\n　し、魔法小ダメージを与え、さらに前方中範囲\n　内の飛行状態のキャラを含む敵すべてに魔法大\n　ダメージを与える。その後【黒星】の数に応じ\n　て、自身のＴＰを回復する。このスキルによる\n　ダメージでは、ＴＰが回復しない。", 1000, 270, 301803, 2.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301900, 1, "ウェアオルカ", 11, 0, 0, 0, 0, "【物理】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。\n\n・前方4キャラに物理中ダメージを\n　与え、物理攻撃力を下げる。\n\n・前方2キャラに魔法小ダメージを\n　与え、ＴＰを下げる。", 1000, 270, 301900, 1.7);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (301901, 1, "キリングオルカ", 11, 0, 0, 0, 0, "【物理】ルナの魔力により凶悪化した大海の狩人。\n鍛え抜いた躰で、刃と化した水流の一撃を放つ。\n\n・前方４キャラに物理大ダメージを与え、\n　物理攻撃力を中ダウンさせる。さらに、\n　味方全員のＴＰ上昇を小アップする。\n・前方小範囲に魔法中ダメージを与え、\n　ＴＰを大ダウン。\n・後ろから２番目のキャラを中心にした\n　小範囲に物理中ダメージを与え、\n　魔法攻撃力を大ダウンさせる。", 1000, 270, 301901, 1.5);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (302000, 1, "ワイルドグリフォン", 11, 0, 0, 0, 0, "【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n【特殊効果】\n・飛行効果無視\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・前から敵５体に魔法ダメージ\n・前から敵５体をノックバック（必中）\n・前から敵５体にスタン（必中）", 1000, 270, 302000, 1.6);
