INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (302106, 1, "ワイバーン", 11, 0, 0, 0, 0, "【物理】大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から１番目の敵と周囲の敵３体までに物理ダ\n　メージ\n・前から１番目の敵と周囲の敵３体までに物理防\n　御力ダウン（必中）\n・自身の物理防御力アップ\n【スキル１】\n・前から敵３体をノックバック\n・前から敵３体に行動速度ダウン\n【通常攻撃】\n・前から敵１体に物理ダメージ", 3000, 270, 302106, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (302200, 2, "イクト・ガラディオン", 16, 0, 0, 0, 0, "【魔法】ルナの魔力で目覚めた、古代の機械兵器。\n無慈悲に放たれる魔力が、侵入者を阻む。\n\n・敵全体に魔法特大ダメージを与え、魔法防御力\n　を大ダウン。\n・前から３キャラに物理大ダメージを与える。\n・前から２キャラに魔法大ダメージを\n　与え、麻痺状態にする。", 1000, 270, 302200, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (302300, 2, "ワイズオウル", 16, 0, 0, 0, 0, "【魔法】大樹の頂に棲まう、聡明なる大梟。\n森に生ける者の英知を束ね、巧みに魔術を操る。\n\n・ユニオンバーストは敵全体に魔法ダメージを\n　与える。\n・ランダムな1キャラに物理ダメージを与え\n　さらに麻痺状態にする。", 1000, 270, 302300, 1.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (302400, 1, "アークガーディアン", 16, 0, 0, 0, 0, "【物理】太古より遺跡を護り続ける人工生命体。\n創造主の秘術で、無限に動力を得て動き続ける。\n\n・ユニオンバーストは敵全体に魔法大ダメージを\n　与える。\n・前方範囲内に物理ダメージを与える。\n・敵全体に魔法ダメージを与え、さらに沈黙させ\n　る。", 1000, 270, 302400, 1.6);
