INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303105, 1, "トウミングマ", 13, 0, 0, 0, 0, "【物理】生涯冬眠すると決め、氷穴に篭る巨大熊。\n日々を夢の中で暮し、獲物を狩る一瞬、覚醒する。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身に魔法ダメージ100%\n　カット効果を付与する。\n・バトル開始時、味方全体のＴＰを中アップ。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理攻撃力と魔法攻撃力を大ダウンさ\n　せる。さらに、敵全体をスタンさせる。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。このスキルによるダメージでは、敵の\n　ＴＰが回復しない。\n・前方３キャラに物理小ダメージを与える。さら\n　に、前方１キャラを吹き飛ばし、凍結状態にし\n　て移動速度を下げる。\n・前方１キャラに物理特大ダメージ。このスキル\n　によるダメージでは、ＴＰ回復量は減少する。", 1000, 270, 303105, 3.5);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303106, 1, "トウミングマ", 13, 0, 0, 0, 0, "【物理】生涯冬眠すると決め、氷穴に篭る巨大熊。\n日々を夢の中で暮し、獲物を狩る一瞬、覚醒する。\n\n・環境効果【寒気凛冽】を発動する。このスキル\n　の効果は敵、味方すべてに適用される。敵、味\n　方全体の行動速度を小ダウンさせる。このスキ\n　ルの効果は他の行動速度変化スキルと重複する。\n　さらに一定時間が経過するごとに、初期値より\n　行動速度が低い状態のキャラに凍結状態を付与\n　する。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン50％カット\n　効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。\n・自身が一度の行動で受けるダメージ値を減衰さ\n　せる。ダメージ値が上昇するほど減衰率も上昇\n　する。\n・バトル開始時、敵全体にＴＰ上昇上限効果を付\n　与する。\n・トウミングマから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与える。さらに、敵全体の物理攻撃力、魔法\n　攻撃力、行動速度を大ダウンさせる。このスキ\n　ルによるダメージでは、敵のＴＰが回復しない。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前方３キャラに物理小ダメージを与える。さら\n　に、前方１キャラをノックバックし、凍結状態\n　にして移動速度を下げる。このスキルの状態異\n　常はダメージを無効化しても付与される。", 1000, 270, 303106, 3.5);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303200, 1, "マダムピック", 6, 0, 0, 0, 0, "【物理】吹雪と共に現れ、\n縄張りを荒らす者には\n大群を呼び寄せ襲い掛かる\n雪国イチのゴージャスマダム。\n\n・前方4キャラに魔法中ダメージを\n　与える。\n\n・ランダムな2キャラに魔法\n　小ダメージを与え、\n　凍結状態にする。", 1000, 270, 303200, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303300, 1, "ガーゴイル", 13, 0, 0, 0, 0, "【物理】朽ちた遺跡に残り\n主亡き地を護る、謎の鳥像。\n街も命令も、全て砂と消えた今は\n誰彼構わず炎を吐き襲い掛かる。\n\n・前方5キャラに魔法中ダメージを\n　与え、火傷状態にする。さらに、\n　魔法防御力を下げる。\n\n・ランダムな3キャラに\n　魔法小ダメージを与え、\n　敵全体を暗闇状態にする。", 1000, 270, 303300, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303301, 1, "ダークガーゴイル", 13, 0, 0, 0, 0, "【物理】暴食の君主に仕える、近衛の黒鉄像。\n冥府を彷徨いし魂を巧みに操り、主に捧げる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・物理攻撃をする敵全体に火傷\n・自身の物理防御力が高いほど：ダメージアップ\n・魔法攻撃をする敵全体に火傷\n・自身の魔法防御力が高いほど：ダメージアップ\n・敵全体にＨＰ吸収ダウン\n【スキル１】\n・敵全体に魔法ダメージ（飛行効果無視）\n・敵全体をノックバック（飛行効果無視・必中）", 1000, 270, 303301, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303302, 1, "ダークガーゴイル", 13, 0, 0, 0, 0, "【物理】暴食の君主に仕える、近衛の黒鉄像。\n冥府を彷徨いし魂を巧みに操り、主に捧げる。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・物理攻撃をする敵全体に火傷\n・自身の物理防御力が高いほど：ダメージアップ\n・魔法攻撃をする敵全体に火傷\n・自身の魔法防御力が高いほど：ダメージアップ\n・敵全体にＨＰ吸収ダウン\n【スキル１】\n・敵全体に魔法ダメージ（飛行効果無視）\n・敵全体をノックバック（飛行効果無視・必中）\n・前方範囲に移動速度ダウン（飛行効果無視・\n　必中）", 1000, 270, 303302, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303303, 1, "サンドガーゴイル", 13, 0, 0, 0, 0, "【物理】塔の魔力を帯び命を宿した砂岩像の魔物。\n砂塵舞う地で敵を待ち、灼炎を吐いて襲い掛かる。\n\n・前方範囲内に魔法大ダメージを与え、火傷状態\n　にする。さらに、魔法防御力を中ダウンさせる。\n・前方３キャラに魔法中ダメージを与え、敵全体\n　を暗闇状態にする。\n・目の前の敵１キャラに物理大ダメージを与える。\n　さらに、敵全体のうち火傷状態の敵には魔法\n　大ダメージを与える。", 1000, 270, 303303, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303304, 1, "アウスグス", 13, 0, 0, 0, 0, "【物理】砂の大地を守護する漆黒\nの悪魔像。広大な砂漠一面を焼き\n尽くすブレスを吐き、灼熱の地を\n進む冒険者の行手を阻む。\n\n・ユニオンバーストは、敵全体に\n　魔法中ダメージを与え、火傷状\n　態にし、さらに魔法防御力を小\n　ダウンさせる。\n　このスキルによるダメージ\n　では、敵のＴＰが回復しない。\n\n・前方の敵１キャラに魔法中\n　ダメージを与える。\n　さらに敵全体に魔法中ダメージ\n　を与え、物理攻撃力と魔法攻撃\n　力を中ダウンさせる。", 1000, 270, 303304, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303305, 1, "ダークガーゴイル", 13, 0, 0, 0, 0, "【物理】暴食の君主に仕える、近衛の黒鉄像。\n冥府を彷徨いし魂を巧みに操り、主に捧げる。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・物理攻撃をする敵全体に火傷（飛行効果無視・\n　ＴＰ回復不可）\n・自身の物理防御力が高いほど：ダメージアップ\n・魔法攻撃をする敵全体に火傷（飛行効果無視・\n　ＴＰ回復不可）\n・自身の魔法防御力が高いほど：ダメージアップ\n・敵全体にＨＰ吸収ダウン（飛行効果無視）\n【スキル１】\n・敵全体に物理ダメージ（ＴＰ回復不可）\n・敵全体をノックバック（必中）\n・前から敵１体に魔法固定ダメージ（ＴＰ回復不\n　可）\n【通常攻撃】\n・前から敵１体に物理ダメージ", 3000, 270, 303305, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303306, 1, "ダークガーゴイル", 13, 0, 0, 0, 0, "【物理】暴食の君主に仕える、近衛の黒鉄像。\n冥府を彷徨いし魂を巧みに操り、主に捧げる。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・物理攻撃をする敵全体に火傷\n・自身の物理防御力が高いほど：ダメージアップ\n・魔法攻撃をする敵全体に火傷\n・自身の魔法防御力が高いほど：ダメージアップ\n・敵全体にＨＰ吸収ダウン\n【スキル１】\n・敵全体に魔法ダメージ\n・敵全体をノックバック（必中）\n・前から敵１体に魔法固定ダメージ", 3000, 270, 303306, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (303400, 1, "スナノヅチ", 13, 0, 0, 0, 0, "【物理】生存競争激しい砂漠の\n頂点に君臨する、巨大トカゲ。\n毒を使い、砂を活かす戦法は、\n過酷な環境で得たサバイバル術。\n\n・敵全体に物理中ダメージを与え、\n　行動速度を下げる。\n　さらに、味方全体の物理攻撃力を\n　上げる。\n\n・前方1キャラを中心にした小範囲\n　に物理小ダメージを与え、毒状態\n　にする。さらに、物理防御力を\n　下げる。", 1000, 270, 303400, 1.9);
