INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (306000, 1, "邪餅神ガミィ", 16, 0, 0, 0, 0, "【物理】邪念の残滓が群集し邪神と化した。\n顕現した煩悩を全て浄化し、新年を迎えよう！\n\n・ユニオンバーストは敵全体に\n　物理大ダメージを与える。\n・前方3キャラに魔法中ダメージを与え、\n　物理攻撃力を下げる。\n・時限式の爆弾モチを前方中距離に召喚する。\n・爆弾モチは、召喚されてから\n　8秒後に大爆発し、範囲内の敵全体に\n　物理中ダメージを与える。\n　ただし、爆発するまでに爆弾モチ自身の\n　ＨＰが50％を切ると、\n　敵全体のＨＰを小回復し、消滅する。\n・邪餅神ガミィ自身のＨＰが\n　50％を切ると、さらに攻撃が激しく\n　凶暴になる。", 2160, 270, 306003, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (306004, 1, "邪餅神ガミィ", 16, 0, 0, 0, 0, "【MODE1】邪念の残滓が群集し邪神と化した。\n顕現した煩悩を全て浄化し、新年を迎えよう！\n\n・邪餅神ガミィ自身が攻撃を受けると、\n　味方全体のＨＰを小回復し、\n　邪餅神ガミィ自身のＨＰを全回復する。", 3000, 270, 306004, 0.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (306005, 1, "邪餅神ガミィ", 16, 0, 0, 0, 0, "【MODE2】邪念の残滓が群集し邪神と化した。\n顕現した煩悩を全て浄化し、新年を迎えよう！\n\n・前方1キャラに物理ダメージを与える。\n・前方範囲内に魔法中ダメージを与え、\n　大きく吹き飛ばす。\n・6個の邪念を込めてボンノウ（赤）を召喚する。\n・3個の邪念を込めてボンノウ（紫）を召喚する。\n・1個の邪念を込めてボンノウ（青）を召喚する。", 3000, 270, 306005, 1.6);
