INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (307504, 1, "紅極鳥", 3, 0, 0, 0, 0, "【物理】塔の魔力を喰らい、暴れ回る巨大魔鳥。\n迂闊に近づくものは、暴風と雷豪が身を引き裂く。\n\n・ユニオンバーストは、敵全体に物理大ダメージ\n　を与える。\n・前から２番目のキャラを中心にした小範囲に物\n　理小ダメージを与え、スタンさせる。\n・前方範囲内の敵に物理中ダメージを与える。", 1000, 270, 307504, 1.8);
