INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310500, 1, "ラウドラパーン", 16, 0, 0, 0, 0, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。", 3000, 270, 310502, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310500, 1, "ラウドラパーン", 16, 0, 0, 0, 0, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・ラウドラパーン自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。", 3000, 270, 310503, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310500, 1, "ラウドラパーン", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】ニュージェネレーションズの\nシャドウを召喚。森で出会った大ウサギ。\n３人は夢をあきらめない心を貫き通せるか、\n大ウサギは各個の弱さを見せつける。\n\n・一番前のキャラに物理ダメージを与える。", 3000, 270, 310504, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310500, 1, "ラウドラパーン", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】森の人気者の歌声に魔物たちが\n大挙する。血沸き肉躍る野生の宴会が今、\n開幕する。\n\n・ブルーゴーレム、スリーピィオウル、\n　ツキツキを倒すと次のＭＯＤＥに移行する。\n\n・ラウドラパーンは、一番前のキャラに\n　物理ダメージを与える。", 3000, 270, 310505, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310500, 1, "ラウドラパーン", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】宴も終焉、大兎が最後の力を振り\n絞る。咆哮にも似た壊震の歌声が、３人の夢道を\n阻む。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 3000, 270, 310506, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310507, 1, "バニフォルテ", 16, 0, 0, 0, 0, "【物理】世にも奇妙な歌う大ウサギ\nの魔獣。その歌声はとても褒められ\nたものではなく、甚だ迷惑である。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた物理割\n　合ダメージを与え、ノックバック\n　させる。その後、目を回している\n　間、自身の物理防御力と魔法防御\n　力を特大ダウン。\n\n・一番前のキャラを中心として、\n　一定範囲に物理中ダメージを与\n　え、さらに行動速度を特大ダウン\n　とスタンさせる。\n\n・前から３キャラに物理小ダメージ\n　を与え、さらに物理防御力を大ダ\n　ウンさせる。", 3000, 270, 310507, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "", 3000, 270, 310600, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。", 3000, 270, 310601, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310600, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。", 3000, 270, 310602, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310603, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・うえきちゃん自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。", 3000, 270, 310603, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310604, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "", 3000, 270, 310604, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310604, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "【MODE１】終に極大化した謎のいきもの。\nまたしても如何ともし難い表情で、大滝の涙が\n大地を濡らしその涙は意思なく皆を蝕んでゆく。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・敵全体のＴＰを上昇させたのち、継続魔法ダメ\n　ージを与えるフィールドを展開する。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。", 3000, 270, 310605, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310604, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "【MODE２】冒険者の抵抗に怯んだか、両葉と\nその巨躯を晒しつつ佇む。だが、その表情は相も\n変わらず、如何ともし難い表情のまま。\n\n・全てのターゲットがブレイク無効。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n", 3000, 270, 310606, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310604, 1, "葛藤のうえきちゃん", 16, 0, 310600, 0, 310600, "【MODE３】その変わらぬ表情は、終にも\n変わらず冒険者は、そのいきものの狂気と畏怖に\n慄く。果たしていきものの謎は解かれるのか…。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。", 3000, 270, 310607, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310700, 1, "なかよしＸ", 16, 0, 0, 0, 0, "", 3000, 270, 310700, 1.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310700, 1, "なかよしＸ", 16, 0, 0, 0, 0, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。", 3000, 270, 310701, 1.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310700, 1, "なかよしＸ", 16, 0, 0, 0, 0, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。", 3000, 270, 310702, 1.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310700, 1, "なかよしＸ", 16, 0, 0, 0, 0, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。\n・なかよしＸ自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。", 3000, 270, 310703, 1.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310700, 1, "なかよしＸ", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】【メルクリウス財団】の\nシャドウを召喚。自分たちの過ちを\n嘲笑うかのように、影の姿で皆を翻弄する。\n\n・自身が毒、猛毒、呪い、火傷の効果を受けると、\n　自身の物理防御力と魔法防御力と行動速度\n　が大ダウンする。\n・目の前の敵１キャラに物理ダメージを与える。", 3000, 270, 310704, 4.2);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310700, 1, "なかよしＸ", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】鋼鉄の球体が、完璧なちぇる語で\n囁きかける。「ちぇる～ん」。心を掴み離さない\n洗脳の言の葉は、確実に世界をちぇるへと誘う。\n\n・ランダムな１キャラに魔法中ダメージを与え、\n　沈黙と誘惑状態にする。", 3000, 270, 310705, 1.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310700, 1, "なかよしＸ", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】鋼鉄の絡繰が再び大地に立つ。\n諸刃の剣・ファイナルモードを解放し、\nなかよし部の３人を追い詰める。\n\n・自身が毒、猛毒、呪い、火傷の効果を受けると、\n　自身の物理防御力と魔法防御力と行動速度\n　が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 3000, 270, 310706, 1.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310707, 1, "なかよしＸ", 16, 0, 0, 0, 0, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n・自身への継続ダメージ量アップ\n・自身に行動速度アップ\n【ユニオンバースト】\n・敵全体に魔法割合ダメージ\n【スキル１】\n・自身に物理攻撃力アップ\n・敵全体に物理ダメージ\n【スキル２】\n・前から敵３体に魔法割合ダメージ\n・前から敵３体に行動速度ダウン", 3000, 270, 310707, 1.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310800, 1, "忍びの頭領ハンゾウ", 16, 0, 0, 0, 0, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。", 3000, 270, 310800, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310800, 1, "忍びの頭領ハンゾウ", 16, 0, 0, 0, 0, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。", 3000, 270, 310801, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310800, 1, "忍びの頭領ハンゾウ", 16, 0, 0, 0, 0, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。自身のＨＰが30%以下の場合、敵全体\n　を猛毒状態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。自身のＨＰ\n　が30%以下の場合、さらに敵全体をスタンさ\n　せる。\n・忍びの頭領ハンゾウ自身のＨＰが30％を切る\n　と、スキルの効果が変化する。", 3000, 270, 310802, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310800, 1, "忍びの頭領ハンゾウ", 16, 0, 0, 0, 0, "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。自身のＨＰが30%以下の場合、敵全体\n　を猛毒状態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。自身のＨＰ\n　が30%以下の場合、さらに敵全体をスタンさ\n　せる。\n・忍びの頭領ハンゾウ自身のＨＰが30％を切る\n　と、スキルの効果が変化する。", 3000, 270, 310803, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310801, 1, "忍びの頭領ハンゾウ", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】【牧場（エリザベスパーク）】の\nシャドウを召喚。幻術で造りだした四農士の幻影\nが皆の帰る場所を再び窮地に陥れる。\n\n・目の前の敵１キャラに物理ダメージを与える。", 3000, 270, 310804, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310800, 1, "忍びの頭領ハンゾウ", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】秘伝の口寄せで忍蛙に化け、\nガマの猛毒をあたり一帯に噴霧する。その猛毒は\nひと吸いで全身を蝕み、徐々に躰を爛れ落とす。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して猛毒を吐き、敵全体を猛毒状\n　態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与え、さらに敵全体\n　をスタンさせる。", 3000, 270, 310805, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310800, 1, "忍びの頭領ハンゾウ", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】最期は、忍びとしての尊厳を取り\n戻し、己が真の力を発揮する。紫電一閃の早業に\n叫声を上げる間もなく屍と化す。\n\n・バトル開始直後に、自身のスキル威力をアップ\n　させる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して猛毒を吐き、敵全体を猛毒状\n　態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与え、さらに敵全体\n　をスタンさせる。", 3000, 270, 310806, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (310900, 1, "ランドソル各地に襲来した謎の円盤", 16, 0, 0, 0, 0, "【物理】平和な収穫祭を急襲する、謎の飛行物体。\nランドソル各地に突如現れ、破壊と略奪を行う。\n\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方１キャラに物理小ダメージ。\n・残りＨＰの高い１キャラに物理小ダメージ。\n・ランダムな１キャラに物理小ダメージ。", 3000, 270, 310900, 0.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311000, 1, "トレントキャッスル", 16, 0, 0, 0, 0, "【物理】塔の魔力で変異し要塞と化した巨大魔樹。\n大地の力を砲弾に変え、堅牢なその身を以って\n破壊の限りを尽くす。\n\n・敵全体に物理大ダメージを与える。\n・範囲内の敵すべてに継続魔法ダメージを与え\n　物理防御力を下げるフィールドを展開する。\n・後ろから２番目のキャラを中心とした小範囲内\n　に物理中ダメージを与え、ＴＰを下げる。\n　その後、味方全体のＴＰを回復する。", 3000, 270, 311000, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311001, 1, "トレントキャッスル", 16, 0, 0, 0, 0, "【物理】深域の魔力で変異し要塞と化した魔樹。\n大地の力を砲弾に変え、破壊の限りを尽くす。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・敵全体にバリア解除\n・物理攻撃をする敵全体に物理ダメージ\n・自身の物理防御力が高いほど：ダメージアップ\n・魔法攻撃をする敵全体に物理ダメージ\n・自身の魔法防御力が高いほど：ダメージアップ\n・敵全体にスタン（必中）\n【スキル１】\n・前から３番目の敵と周囲に魔法ダメージ\n・前から３番目の敵を中心に物理防御力ダウン\n　フィールド\n・ダメージを与えた対象が少ないほど：ダウン量\n　上昇", 3000, 270, 311001, 2.8);
