INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311100, 1, "黒の王ジャバウォック", 16, 0, 0, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰをダウンさせる。", 1000, 270, 311100, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311100, 1, "黒の王ジャバウォック", 16, 0, 0, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。", 1000, 270, 311101, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311100, 1, "黒の王ジャバウォック", 16, 0, 0, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度をダウンさせる。", 1000, 270, 311102, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311100, 1, "黒の王ジャバウォック", 16, 0, 0, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度をダウンさせる。\n・黒の王ジャバウォック自身のＨＰが35％を切\n　ると、さらに攻撃が激しく凶暴になる。", 1000, 270, 311103, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311101, 1, "黒の王ジャバウォック", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】瘴黒の幕を身に纏い、黒の王が再\n臨する。闇と混沌を集約した障壁は、救世主の光\nの一射をも耐えるという。\n\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージを与えるたびに徐々に耐久性\n　を失っていき、2400000ダメージを与え\n　るとバリアは破壊される。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。", 1000, 270, 311104, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311100, 1, "黒の王ジャバウォック", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】リノ、アユミ、ヒヨリのシャドウ\nを召喚。心の闇が具現化し、自己の弱さを露見さ\nせる。希望の心を取り戻せるか？今、問われる。\n\n・目の前の敵１キャラに物理ダメージを与える。", 1000, 270, 311105, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311100, 1, "黒の王ジャバウォック", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】黒の王が終の力を解放し、世界は\n常闇へと堕ちていく。本の殆どの頁は黒に染まり\n救世主の物語は終焉を迎えようとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰをダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 1000, 270, 311106, 2.1);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311200, 1, "フラワーシュバリエ", 16, 0, 0, 0, 0, "【物理】大輪の花に擬態する魔物。\nその美しさに惹かれた、不用意な\n獲物の前に絶望の光が花開く。\n\n・前方３キャラに物理大ダメージを\n　与え、さらに敵全体に物理大\n　ダメージを与える。\n\n・前方２キャラに物理小ダメージを\n　与え、さらに自分の物理攻撃力を\n　中アップさせる。\n\n・前から１番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。\n\n・前から２番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。", 3000, 270, 311200, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311200, 1, "フラワーシュバリエ", 16, 0, 0, 0, 0, "【物理】大輪の花に擬態する魔物。\nその美しさに惹かれた、不用意な\n獲物の前に絶望の光が花開く。\n\n・前方３キャラに物理大ダメージを\n　与え、さらに敵全体に物理大\n　ダメージを与える。\n\n・前方２キャラに物理小ダメージを\n　与え、さらに自分の物理攻撃力を\n　中アップさせる。\n\n・前から１番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。\n\n・前から２番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。", 3000, 270, 311201, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311202, 1, "フラワーシュバリエ", 16, 0, 0, 0, 0, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・敵全体に魔法ダメージ（飛行効果無視）\n・与えた物理ダメージが高いほど：魔法ダメージ\n　量アップ\n【スキル１】\n・自身に物理攻撃力アップ\n・自身に物理／魔法防御力アップ\n・自身にＴＰ回復\n【通常攻撃】\n・前から１番目の敵と周囲の敵１体に物理\n　ダメージ", 3000, 270, 311202, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311203, 1, "フラワーシュバリエ", 16, 0, 0, 0, 0, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・敵全体に魔法ダメージ（飛行効果無視）\n・与えた物理ダメージが高いほど：魔法ダメージ\n　量アップ\n【スキル１】\n・自身に物理攻撃力アップ\n・自身に物理／魔法防御力アップ\n・自身にＴＰ回復\n【通常攻撃】\n・前から１番目の敵と周囲の敵１体に物理\n　ダメージ", 3000, 270, 311203, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311204, 1, "フラワーシュバリエ", 16, 0, 0, 0, 0, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n・環境効果【百花繚乱】を発動する。このスキル\n　の効果は敵、味方すべてに適用される。一定時\n　間が経過するごとに、敵、味方全体のＴＰを中\n　回復させる。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。\n・自身が一度の行動で受けるダメージ値を減衰さ\n　せる。ダメージ値が上昇するほど減衰率も上昇\n　する。\n・バトル開始時、敵全体にＴＰ上昇上限効果を付\n　与する。\n・フラワーシュバリエから受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に物理特大ダメージを与える。\n　その後、敵全体にこのスキルで与えたダメージ\n　量に応じた物理ダメージを与える。このスキル\n　による全体ダメージでは、ＴＰが回復しない。\n・自身の物理攻撃力と物理防御力と魔法防御力を\n　特大アップさせ、ＴＰを中回復する。\n・通常攻撃は、最も近い敵１キャラを中心にした\n　小範囲内の敵最大２キャラに物理特大ダメージ\n　を与える。", 3000, 270, 311204, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311300, 2, "覚醒・浮気星", 16, 0, 0, 0, 0, "", 3000, 270, 311300, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311300, 2, "覚醒・浮気星", 16, 0, 0, 0, 0, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番後ろの敵を中心として、一定範囲に\n　魔法小ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法小ダメージを与える。\n　さらに誘惑状態にして物理攻撃力と魔法攻撃力\n　を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。", 3000, 270, 311301, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311300, 2, "覚醒・浮気星", 16, 0, 0, 0, 0, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番後ろの敵を中心として、一定範囲に\n　魔法小ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法小ダメージを与える。\n　さらに誘惑状態にして物理攻撃力を小アップ、\n　魔法攻撃力を中アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。", 3000, 270, 311302, 3.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311300, 2, "覚醒・浮気星", 16, 0, 0, 0, 0, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・敵全体にキャラの最大ＨＰに応じた魔法割合\n　ダメージを与える。\n・一番後ろの敵を中心として、一定範囲に\n　魔法中ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を大アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法中ダメージを与える。\n　さらに誘惑状態にして物理攻撃力と魔法攻撃力\n　を大アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。", 3000, 270, 311303, 3.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311300, 2, "覚醒・浮気星", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】自身のハーレムドリームを築き\n上げるため手当たり次第、乙女たちを強制的に\n惑わす。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　ダメージを与えた後、浮気星が保持している\n　【ふた心】の数は０になる。\n・敵全体にキャラの最大ＨＰに応じた魔法割合\n　ダメージを与える。\n・挑発を無視して、最も後ろの敵１キャラに魔法\n　ダメージを与え、さらに引き寄せる。\n・一番前の敵を中心として、一定範囲に\n　魔法ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力をアップさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　このスキルの攻撃対象の数だけ、浮気星に\n　一定時間【ふた心】を追加する。", 3000, 270, 311304, 3.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311300, 2, "覚醒・浮気星", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】綺羅星すらも霞ませると、己の\n輝きに自信をのぞかせ、その盲信と乙女への思い\nが銀河を巻き込み、全てを幻惑させる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが60％未満の場合、\n　さらに与えるダメージがアップする。\n　ダメージを与えた後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番前の敵を中心として、一定範囲に\n　魔法ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力をアップさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　このスキルの攻撃対象の数だけ、浮気星に\n　一定時間【ふた心】を追加する。", 3000, 270, 311305, 3.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311300, 2, "覚醒・浮気星", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】夢見たハーレムが潰え、全てを\n無に還そうと真の力を解放する。銀河の星々を\n天の川に沈めるべく、全ての下心をブチ撒ける。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、バトル開始直後に、さらに与えるダメー\n　ジがアップする。\n　ダメージを与えた後、浮気星が保持している\n　【ふた心】の数は０になる。\n・敵全体にキャラの最大ＨＰに応じた魔法割合\n　ダメージを与える。\n・一番後ろの敵を中心として、一定範囲に\n　魔法ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力をアップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法ダメージを与える。\n　さらに誘惑状態にして物理攻撃力と魔法攻撃力\n　をアップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。", 3000, 270, 311306, 3.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311400, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "", 3000, 270, 311400, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311400, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力をダウンさせ、物理攻撃力をアップさ\n　せる。\n・最も近い敵１キャラに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷、呪詛の効果を\n　受けていると、自身の物理防御力と魔法防御力\n　がアップする時間が短くなる。", 3000, 270, 311401, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311400, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力をダウンさせ、物理攻撃力をアップさ\n　せる。\n・最も近い敵１キャラに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷、呪詛の効果を\n　受けていると、自身の物理防御力と魔法防御力\n　がアップする時間が短くなる。\n・鮫吉自身のＨＰが20％を切ると、敵全体に\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、一定時間、ユニオンバーストのみ使用で\n　きない状態にし、魔法攻撃力をダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。", 3000, 270, 311402, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311400, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力を大ダウンさせ、物理攻撃力をアップ\n　させる。\n・最も近い敵１キャラに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷、呪詛の効果を\n　受けていると、自身の物理防御力と魔法防御力\n　がアップする時間が短くなる。\n・鮫吉自身のＨＰが20％を切ると、敵全体に\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、一定時間、ユニオンバーストのみ使用で\n　きない状態にし、魔法攻撃力をダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。", 3000, 270, 311403, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311404, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】シマを荒らす不届きモノには、幾\n千もの抗争を潜り抜けた鋼の肉体から繰り出す、\n全身全霊のガチンコタックルをブチかます。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力をダウンさせ、物理攻撃力をアップさ\n　せる。\n・前方範囲内の敵すべてに物理大ダメージを与え、\n　物理防御力を大ダウンさせる。\n・前方３キャラに物理大ダメージを与える。さら\n　に、自身にも物理ダメージを与え、物理防御力\n　と魔法防御力を一定時間極大ダウンさせる。", 3000, 270, 311404, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311405, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】圧倒的なカリスマ性と絶望的な暴\n力につき従う、舎弟たちが集結する。海洋族の精\n鋭によるカチコミが、いま幕を開ける。\n\n・鮫吉は目の前の敵１キャラへの物理攻撃を行う。\n・前方３キャラに物理大ダメージを与える。さら\n　に、自身にも物理ダメージを与え、物理防御力\n　と魔法防御力を一定時間極大ダウンさせる。", 3000, 270, 311405, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311404, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】獰猛な雄叫びとともに、暴君は追\n撃を繰り出す。刃向かう者を完膚なきまでに殴り\n潰すまで、降り注ぐ轟拳の嵐は決してやまない。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力を大ダウンさせ、物理攻撃力をアップ\n　させる。\n・前方範囲内の敵すべてに物理大ダメージを与え、\n　物理防御力をダウンさせる。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　鮫吉が毒、猛毒、呪い、火傷の効果を受けてい\n　ると、自身の物理防御力と魔法防御力がアップ\n　する時間が短くなる。\n・鮫吉自身のＨＰが10％を切ると、敵全体に\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、一定時間、ユニオンバーストのみ使用で\n　きない状態にし、魔法攻撃力をダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。", 3000, 270, 311406, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311407, 1, "レイジングジョー", 16, 0, 0, 0, 0, "【物理】塔の魔力で狂化した、海洋族の狂戦士。\n獰猛な雄叫びとともに、猛々しく拳撃を繰り出す。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理\n　防御力を大ダウンさせ、さらに物理攻撃力を\n　中アップさせる。\n・最も近い１キャラを中心とした小範囲内の敵す\n　べてに物理大ダメージを与え、物理防御力を大\n　ダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体をノックバックさせる。その後一定時間、\n　雷を身にまとい、自身の物理防御力と魔法防御\n　力を極大アップさせる。\n　レイジングジョーが毒、猛毒、呪い、呪詛、火\n　傷の効果を受けていると、自身の物理防御力と\n　魔法防御力がアップする時間が短くなる。\n・レイジングジョー自身のＨＰが40％を切ると、\n　敵全体にキャラの最大ＨＰに応じた物理割合ダ\n　メージを与え、一定時間、ユニオンバーストの\n　み使用できない状態にする。\n　さらにバトル中、魔法攻撃力を大ダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　さらに攻撃が激しく凶暴になる。", 3000, 270, 311407, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311408, 1, "レイジングジョー", 16, 0, 0, 0, 0, "【物理】深域の魔力で狂化した、海洋族の狂戦士。\n獰猛な雄叫びとともに、猛々しく拳撃を繰り出す。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から敵１体に物理ダメージ（召喚体無視）\n・敵全体に物理ダメージ（召喚体無視・ＴＰ回復\n　不可）\n・このスキルで与えたダメージが大きいほど：ダ\n　メージアップ\n【スキル１】\n・前から敵１体に物理ダメージ\n・前から敵１体に物理防御力ダウン（必中）\n【通常攻撃】\n・前から敵１体に物理ダメージ", 3000, 270, 311408, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311409, 1, "渚の暴君・鮫吉", 16, 0, 0, 0, 0, "【物理】ビーチ一帯の近海をシメる海洋族の暴君。\n売られた喧嘩には、ただの一度も背を向けず、\nシマを荒らすヤツは絶対に許さない。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に物理割合ダメージ（ＴＰ回復不可）\n・敵全体に物理防御力ダウン（必中）\n・自身に物理攻撃力アップ\n【スキル１】\n・前方範囲に物理ダメージ\n・前方範囲に物理防御力ダウン（必中）\n【スキル２】\n・敵全体をノックバック\n・自身の物理／魔法防御力アップ\n・自身が継続ダメージ状態の場合：\n　持続時間ダウン\n【通常攻撃】\n・前から敵１体に物理ダメージ\n【ＨＰ20％以下時発動】\n・敵全体に物理割合ダメージ（ＴＰ回復不可）\n・敵全体に魔法攻撃力ダウン（必中）\n・敵全体にＵＢ封印（必中）\n・自身に物理攻撃力アップ", 3000, 270, 311409, 2.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311500, 2, "暴走のイノセントボウ", 16, 0, 0, 0, 0, "", 3000, 270, 311500, 2.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311500, 2, "暴走のイノセントボウ", 16, 0, 0, 0, 0, "【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに最大ＨＰに応じた魔法\n　割合ダメージを与え、さらにＨＰが60％を\n　切ったキャラを一定時間エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。", 3000, 270, 311501, 2.4);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (311500, 2, "暴走のイノセントボウ", 16, 0, 0, 0, 0, "【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに最大ＨＰに応じた魔法\n　割合ダメージを与え、さらにＨＰが60％を\n　切ったキャラを一定時間エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度を中ダウンさせる。\n・イノセントボウ自身のＨＰが50％を切ると、\n　魔法攻撃力が小アップし、さらに攻撃が激しく\n　凶暴になる。", 3000, 270, 311502, 2.4);
