INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312600, 1, "ジェネラルホーン", 16, 0, 0, 0, 0, "", 3000, 270, 312600, 2.7);
