INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312700, 2, "黒風の精霊", 16, 0, 0, 0, 0, "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。", 3000, 270, 312701, 2.7);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312700, 2, "黒風の精霊", 16, 0, 0, 0, 0, "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\n　力を大ダウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 3000, 270, 312702, 2.7);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312700, 2, "黒風の精霊", 16, 0, 0, 0, 0, "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\n　力を大ダウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 3000, 270, 312703, 2.7);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312700, 2, "黒風の精霊", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】第一の試練。黒き風の精霊の暴風\nの継ぎ目を見切れば、自ずと己の進むべき道が\n切り拓かれん。\n\n・敵全体に物理ダメージとスタンを与え、ノック\n　バックさせる。\n　黒風の精霊が眠っている間、自身の物理防御力\n　が大幅にダウンし、その間に100000以上\n　のダメージを与えると、防御力ダウンが延長さ\n　れ行動速度もダウンする。", 3000, 270, 312704, 2.7);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312705, 2, "黒風の精霊", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】第二の試練。己の弱き心と対峙\nせよ。いつも答えは己の内にある。今一度、仲間\nの力を信じ立ち向かえ。\n\n・前から２キャラに物理小ダメージを与え、さら\n　にノックバックさせ、スタンを与える。", 3000, 270, 312705, 2.7);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312700, 2, "黒風の精霊", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】第三の試練。終の試練は、己の\nすべてを懸け黒き風の精霊に本当の想いと絆を\n顕現し、己の想いを確かめよ。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\n　力を大ダウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 3000, 270, 312706, 2.7);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312800, 1, "魔物の姫", 16, 0, 0, 0, 0, "", 3000, 270, 312800, 1.8);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (312800, 1, "魔物の姫", 16, 0, 0, 0, 0, "【物理】辺境の城に座する、孤高な魔物の姫君。\n悲哀なる恋慕を胸に、自らに挑む者を打ち砕く。\n\n・ユニオンバーストは、前から２キャラに\n　最大ＨＰに応じた物理割合ダメージを与える。\n　力を溜めることに成功した場合、ダメージが\n　アップし、敵全体を畏怖状態にする。\n　畏怖状態のキャラは、一定時間攻撃およびＨＰ\n　回復のユニオンバーストとスキル、強化通常攻\n　撃の効果がダウン。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・物理防御力と魔法防御力が大幅にダウンした後、\n　一定時間、力を溜める。\n　力が溜まると、ＴＰが特大回復する。\n　力を溜めている間に魔物の姫が7000以上の\n　ダメージを受けると、力を溜めることに失敗し、\n　ＴＰが大ダウン。 \n・目の前の敵１キャラに物理ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・前から３キャラに最大ＨＰに応じた物理割合\n　ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。", 3000, 270, 312801, 1.8);
