INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313705, 2, "ミソラ", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】戦いを止められるのは黒い天使\nのみ。大切なものを守るため、冒険者として踏み\n出すため…その迷いなき翼で巨悪を薙ぎ払え！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ミソラから受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与える。\n・前から１番目、２番目、３番目の敵に魔法小ダ\n　メージを与える。\n・敵全体の魔法防御力を中ダウンさせ、火傷状態\n　を付与する。敵のＨＰが50％未満の場合、混\n　乱状態を付与する。このスキルにより付与され\n　た火傷のダメージでは、ＴＰが回復しない。", 3000, 270, 313707, 2.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313708, 1, "ミソラ", 16, 0, 0, 0, 0, "【物理】常に明朗快活、誰とでも友達になれる溌\n溂少女。回転式銃を愛用し、単独で飽和攻撃を仕\n掛ける。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・ランダムな敵１体に物理ダメージ×７回\n・ぺんぽこりんに物理攻撃力アップ\n・ぺんぽこりんに行動速度アップ\n【スキル１】\n・ぺんぽこりんにＨＰ回復\n【スキル２】\n・ぺんぽこりん召喚\n・前方範囲をノックバック\n【通常攻撃】\n・ランダムな敵１体に物理ダメージ×３回\n【召喚体スキル１】\n・前から敵１体に物理ダメージ", 3000, 270, 313708, 2.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313709, 1, "ミソラ", 16, 0, 0, 0, 0, "【物理】常に明朗快活、誰とでも友達になれる溌\n溂少女。回転式銃を愛用し、単独で飽和攻撃を仕\n掛ける。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・ランダムな敵１体に物理ダメージ×７回\n・ぺんぽこりんに物理攻撃力アップ\n・ぺんぽこりんに行動速度アップ\n【スキル１】\n・ぺんぽこりんのＨＰを回復\n【スキル２】\n・ぺんぽこりんを召喚\n・敵全体をノックバック\n【通常攻撃】\n・ランダムな敵１体に物理ダメージ×３回\n【召喚体スキル１】\n・前から敵１体に物理ダメージ（ＴＰ回復減少）", 3000, 270, 313709, 2.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313800, 1, "アゾールド", 16, 0, 0, 0, 0, "常に幾つもの奥の手を隠し持つ、大食の紳士。\n万能人形を駆使し、知略戦略で敵を追い詰める。\n\n・ユニオンバーストは、敵全体にアゾールドの物\n　理攻撃力に応じた物理割合ダメージを与える。\n　さらに、アゾールドの物理防御力と魔法防御力\n　を大ダウン。\n・万能人形を召喚し敵全体をノックバックさせる。\n　万能人形は前から１番目のキャラに物理小ダメー\n　ジを与える。\n　万能人形のＨＰが一定値以下になった時、アゾー\n　ルドの物理防御力と魔法防御力を大ダウンさせ\n　る。\n・前から１番目のキャラに物理小ダメージ。", 3000, 270, 313800, 1.5);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313801, 1, "アゾールド", 16, 0, 0, 0, 0, "常に幾つもの奥の手を隠し持つ、大食の紳士。\n万能人形を駆使し、知略戦略で敵を追い詰める。\n\n・ユニオンバーストは、敵全体にアゾールドの物\n　理攻撃力に応じた物理割合ダメージを与える。\n　さらに、アゾールドの物理防御力と魔法防御力\n　を大ダウン。\n・万能人形を召喚し敵全体をノックバックさせる。\n　万能人形は前から１番目のキャラに物理小ダメー\n　ジを与える。\n　万能人形のＨＰが一定値以下になった時、アゾー\n　ルドの物理防御力と魔法防御力を大ダウンさせ\n　る。\n・前から１番目のキャラに物理小ダメージ。", 3000, 270, 313801, 1.5);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313802, 1, "アゾールド", 16, 0, 0, 0, 0, "【物理】常に幾つもの奥の手を隠し持つ、大食の\n紳士。万能人形を駆使し、知略戦略で敵を追い詰\nめる。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・万能人形の数が多いほど：ダメージアップ\n・自身に物理／魔法防御力ダウン\n【スキル１】\n・万能人形３体召喚\n・敵全体をノックバック\n【通常攻撃】\n・前から敵１体に物理ダメージ\n【召喚体特殊効果】\n・自身のＨＰが１％になったとき：\n　アゾールドに物理／魔法防御力ダウン\n【召喚体スキル１】\n・前から敵１体に物理ダメージ", 3000, 270, 313802, 1.5);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313900, 1, "ゼーン", 16, 0, 313900, 0, 313900, "一騎当千、万夫不当の実力を揮う寡黙な剣士。\n覚醒することで、龍は更なる高みへと昇る。\n\n・ユニオンバーストは、敵全体にゼーンの物理\n　攻撃力と残りＨＰに応じた物理割合ダメージ\n　を与える。\n　このスキルによるダメージでは、ＴＰ回復量\n　は減少する。\n・敵全体に物理中ダメージを与え、スタンさせ\n　る。\n・ゼーンのＴＰ上昇、行動速度、物理攻撃力を\n　大アップ。\n　このスキルの効果は発動するたびにアップす\n　る。\n　３回目の発動時は、さらにＴＰ1000アップ。\n・前から１番目のキャラに物理中ダメージを与\n　え、小さく吹き飛ばす。", 3000, 270, 313900, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313901, 1, "ゼーン", 16, 0, 313900, 0, 313900, "", 3000, 270, 313901, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (313902, 1, "ゼーン", 16, 0, 313900, 0, 313900, "【物理】一騎当千、万夫不当の実力を揮う寡黙な\n剣士。覚醒することで、龍は更なる高みへと昇る。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に物理割合ダメージ（飛行効果無視・\n　ＴＰ回復不可）\n・自身の物理攻撃力が高いほど：ダメージアップ\n・敵全体に物理／魔法攻撃力ダウン（飛行効果無\n　視・必中）\n【スキル１】\n・敵全体に物理ダメージ（ＴＰ回復不可）\n・敵全体にスタン（必中）\n【スキル２】\n・前から敵１体に物理ダメージ\n・前から敵１体をノックバック（必中）\n【スキル３】\n・自身にＴＰ上昇アップ\n・自身に行動速度アップ\n・自身に物理攻撃力アップ\n・発動するたび：効果量アップ\n・３回目の発動時：ＴＰ上昇アップの代わりに\n　ＴＰ回復", 3000, 270, 313902, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314000, 1, "宿題", 16, 0, 0, 0, 0, "", 3000, 270, 314000, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314001, 1, "得意な宿題", 16, 0, 0, 0, 0, "まずは手始めに得意な宿題を終わらせよう。\nここでつまづいてはいられない。夏休みの宿題は\nほかにもまだまだあるぞ！！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 3000, 270, 314001, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314002, 1, "ちょっと苦手な宿題", 16, 0, 0, 0, 0, "誰にでもある苦手科目の宿題。ついつい後回しに\nしがちで、溜まってしまうとなかなか大変。\nここで立ち止まるわけにはいかないぞ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 3000, 270, 314002, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314003, 1, "すごく苦手な宿題", 16, 0, 0, 0, 0, "眺めているだけで眩暈のするいや～な宿題。\nペンを置いて外に飛び出したくなる衝動を耐え、\nコツコツ進めていくことが攻略のカギ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 3000, 270, 314003, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314004, 1, "夏休み最後まで残りし宿題", 16, 0, 0, 0, 0, "SP MODE1", 3000, 270, 314004, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314005, 1, "夏休み最後まで残りし宿題", 16, 0, 0, 0, 0, "SP MODE2", 3000, 270, 314005, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314006, 1, "夏休み最後まで残りし宿題", 16, 0, 0, 0, 0, "夏休みの間ずっと、目を背けてきた最後の宿題。\nここまで放置してきた後悔が一気に襲い掛かる。\nすべてやり遂げて笑顔で登校できるか！？\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 3000, 270, 314006, 1.0);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314100, 2, "クイーンウタクラゲ", 16, 0, 0, 0, 0, "", 3000, 270, 314100, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314100, 2, "クイーンウタクラゲ", 16, 0, 0, 0, 0, "【魔法】華のように大海をゆらめき、歌声を奏で\nるウタクラゲの女王。美しく飾られたその姿の内\nには猛毒の体液を隠し持つ。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。猛毒状\n　態の敵には追加で最大ＨＰに応じた魔法割合ダ\n　メージを与える。さらに敵全体に毒状態を付与\n　し、既に毒か猛毒状態の敵には猛毒状態を付与\n　する。\n・最も近い敵１キャラに魔法大ダメージを与える。\n・敵全体に魔法小ダメージを与え、毒状態を付与\n　する。敵が猛毒状態の場合、スタンさせる。\n・クイーンウタクラゲの付与する毒および猛毒の\n　ダメージでは、キャラのＴＰが回復しない。", 3000, 270, 314101, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314100, 2, "クイーンウタクラゲ", 16, 0, 0, 0, 0, "【魔法】華のように大海をゆらめき、歌声を奏で\nるウタクラゲの女王。美しく飾られたその姿の内\nには猛毒の体液を隠し持つ。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。猛毒状\n　態の敵には追加で最大ＨＰに応じた魔法割合ダ\n　メージを与える。さらに敵全体に毒状態を付与\n　し、既に毒か猛毒状態の敵には猛毒状態を付与\n　する。\n・最も近い敵１キャラに魔法大ダメージを与える。\n・敵全体に魔法小ダメージを与え、毒状態を付与\n　する。敵が猛毒状態の場合、スタンさせる。\n・クイーンウタクラゲの付与する毒および猛毒の\n　ダメージでは、キャラのＴＰが回復しない。", 3000, 270, 314102, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314100, 2, "クイーンウタクラゲ", 16, 0, 0, 0, 0, "【魔法】華のように大海をゆらめき、歌声を奏で\nるウタクラゲの女王。美しく飾られたその姿の内\nには猛毒の体液を隠し持つ。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。猛毒状\n　態の敵には追加で最大ＨＰに応じた魔法割合ダ\n　メージを与える。さらに敵全体に毒状態を付与\n　し、既に毒か猛毒状態の敵には猛毒状態を付与\n　する。\n・最も近い敵１キャラに魔法大ダメージを与える。\n・敵全体に魔法小ダメージを与え、毒状態を付与\n　する。敵が猛毒状態の場合、スタンさせる。\n・クイーンウタクラゲの付与する毒および猛毒の\n　ダメージでは、キャラのＴＰが回復しない。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 3000, 270, 314103, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314104, 2, "クイーンウタクラゲ", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】襲い来る無数の魔物たち。その歌\n声は轟然とこだまし舞台を脅かす。ファンの笑顔\nを守るため、少女たちは歌い踊り、そして戦う。\n\n・バトル開始時に、ウタクラゲを３体召喚し\n　敵全体をノックバックさせる。\n・ウタクラゲを１体倒すたびに、残りウタクラゲ\n　数が１減少する。\n　その後、ウタクラゲが再度召喚され、敵全体を\n　ノックバックさせる。\n・ウタクラゲは、残りＨＰが最も高い敵１キャラ\n　に魔法中ダメージを与え、毒状態を付与する。\n・ユニオンバーストは、敵全体にキャラのＨＰに\n　応じた魔法割合ダメージを与える。\n　猛毒状態の敵にはダメージがアップする。\n　さらに敵に毒状態を付与し、既に毒か猛毒状態\n　の敵には猛毒状態を付与する。\n・目の前の敵１キャラに魔法大ダメージを与える。\n・クイーンウタクラゲとウタクラゲの付与する毒\n　および猛毒のダメージでは、キャラのＴＰが回\n　復しない。", 3000, 270, 314104, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314105, 2, "クイーンウタクラゲ", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】目の前に現れたのは、過去の\n自分たち。成長の果て、少女たちは過去を越え、\nその一瞬一瞬の輝きを歌声に乗せる。\n\n・カルミナのシャドウを召喚。\n・目の前の敵１キャラに魔法大ダメージを与える。", 3000, 270, 314105, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314104, 2, "クイーンウタクラゲ", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】優艶なる華は乱れ、内なる魔性\nを解き放つ。劇毒の雨音をメロディーに、\n歌姫は歌う――最期の歌を。\n\n・ユニオンバーストは、敵全体にキャラのＨＰに\n　応じた魔法割合ダメージを与える。\n　猛毒状態の敵にはダメージがアップする。\n　さらに敵に毒状態を付与し、既に毒か猛毒状態\n　の敵には猛毒状態を付与する。\n・目の前の敵１キャラに魔法大ダメージを与える。\n・敵全体に魔法小ダメージを与え、毒状態を付与\n　する。\n　敵が猛毒状態の場合、スタンさせる。\n・クイーンウタクラゲの付与する毒および猛毒の\n　ダメージでは、キャラのＴＰが回復しない。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 3000, 270, 314106, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314200, 1, "ドクターギグル", 16, 0, 0, 0, 0, "", 3000, 270, 314200, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (314200, 1, "ドクターギグル", 16, 0, 0, 0, 0, "【物理】卑劣なる本性を現したオーエドの怪医。\n研究で手にした究極の肉体で、世界への復讐を\n果たさんとする。\n\n・ユニオンバーストは、敵全体にドクターギグル\n　の物理攻撃力に応じた物理割合ダメージを与え、\n　毒状態にする。\n・デモンフラスコを召喚し敵全体をノックバック\n　させる。\n・デモンフラスコは、一定時間経過するとドク\n　ターギグルの物理攻撃力を中アップさせ、ＴＰ\n　を特大回復させたあと、消滅する。ただしデモ\n　ンフラスコのＨＰが一定値以下になった場合は、\n　敵全体の物理攻撃力と魔法攻撃力を特大アップ\n　させ、ＴＰを特大回復させたあと、消滅する。\n　またその時に、次にドクターギグルが召喚する\n　デモンフラスコが１段階強化される。\n・物理攻撃力、魔法攻撃力いずれかで、攻撃力が\n　最も高い敵１キャラに物理小ダメージを与え、\n　さらに行動速度を中ダウンさせる。このスキル\n　の状態異常はダメージを無効化しても付与され\n　る。", 3000, 270, 314201, 1.6);
