INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (315200, 1, "讐鼠・ステゴロのチュウザ", 16, 0, 0, 0, 0, "【物理】ネズミ一家の頼れる若頭。かつて拳一つ\nで漢を見せた猛き闘鼠は、一家の危機を救うため、\nヒカリモノに手を染めて抗争に身を投じる。\n\n・ユニオンバーストは、前から１番目のキャラを\n　中心とした範囲内に物理大ダメージ。\n・前から１番目のキャラに物理中ダメージを与え、\n　物理防御力を中ダウンさせる。\n・前から１番目のキャラに物理小ダメージ。", 3000, 270, 315201, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (315200, 1, "讐鼠・ステゴロのチュウザ", 16, 0, 0, 0, 0, "【物理】ネズミ一家の頼れる若頭。かつて拳一つ\nで漢を見せた猛き闘鼠は、一家の危機を救うため、\nヒカリモノに手を染めて抗争に身を投じる。\n\n・ユニオンバーストは、前から１番目のキャラを\n　中心とした範囲内に物理大ダメージ。\n・前から１番目のキャラに物理中ダメージを与え、\n　物理防御力を中ダウンさせる。\n・前から１番目のキャラに物理小ダメージ。", 3000, 270, 315202, 1.6);
