INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316100, 2, "レインスピリット", 16, 0, 0, 0, 0, "【魔法】塔の魔力が集まり形を成した冷酷な水霊。\n吹き荒れる暴雨は、無慈悲に冒険者を飲み込む。\n\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを\n　与えて吹き飛ばす。\n　さらに、前方範囲内の敵すべてにキャラの\n　最大ＨＰに応じた追加の魔法割合ダメージを\n　与える。\n・前方範囲内の敵すべてに魔法中ダメージを与え、\n　小さく吹き飛ばす。さらに最も近い敵１キャラ\n　に追加の魔法中ダメージを与える。\n・一番遠くの敵１キャラに魔法中ダメージを与え、\n　さらに小さく引き寄せる。\n・レインスピリット自身のＨＰが40%を切ると、\n　自分の魔法防御力を特大アップさせ、\n　さらに攻撃が激しく狂暴になる。", 3000, 270, 316100, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316101, 2, "レインスピリット", 16, 0, 0, 0, 0, "【魔法】深域の魔力が集まり形成した冷酷な水霊。\n吹き荒れる暴雨は、無慈悲に冒険者を飲み込む。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に魔法ダメージ（ＴＰ回復不可）\n・対象のＨＰ70％以下：前方範囲の敵をノック\n　バック、後方範囲の敵を引き寄せる（必中）\n【スキル１】\n・後ろから１番目の敵に魔法割合ダメージ（挑発\n　無視）\n・後ろから１番目の敵を引き寄せる（挑発無視・\n　必中）\n・後ろから１番目の敵に行動速度ダウン（挑発無\n　視・必中）\n【通常攻撃】\n・前から１番目の敵と周囲に魔法ダメージ", 3000, 270, 316101, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316102, 2, "レインスピリット", 16, 0, 0, 0, 0, "【魔法】魔力の雨が集まり形を成した冷酷な水霊。\n吹き荒れる暴雨は、無慈悲に冒険者を飲み込む。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・飛行効果無視\n【ユニオンバースト】\n・敵全体に魔法ダメージ\n・敵全体にスタン（必中）\n・前方範囲ＨＰ70％未満：ノックバック（必中）\n・後方範囲ＨＰ70％未満：引き寄せ（必中）\n【スキル１】\n・後ろから敵１体に魔法ダメージ（挑発無視）\n・後ろから敵１体を引き寄せる（挑発無視・必中）\n・後ろから敵１体に行動速度ダウン（挑発無視・\n　必中）\n【通常攻撃】\n・前から１番目の敵と周囲に魔法ダメージ\n・前から１番目の敵と周囲に物理／魔法攻撃力ダ\n　ウン（必中）", 3000, 270, 316102, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316103, 2, "レインスピリット", 16, 0, 0, 0, 0, "【魔法】魔力の雨が集まり形を成した冷酷な水霊。\n吹き荒れる暴雨は、無慈悲に冒険者を飲み込む。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n・飛行効果無視\n【ユニオンバースト】\n・敵全体に魔法ダメージ\n・敵全体にスタン（必中）\n・前方範囲ＨＰ70％未満：ノックバック（必中）\n・後方範囲ＨＰ70％未満：引き寄せ（必中）\n【スキル１】\n・後ろから敵１体に魔法ダメージ（挑発無視）\n・後ろから敵１体を引き寄せる（挑発無視・必中）\n・後ろから敵１体に行動速度ダウン（挑発無視・\n　必中）\n【通常攻撃】\n・前から１番目の敵と周囲に魔法ダメージ\n・前から１番目の敵と周囲に物理／魔法攻撃力ダ\n　ウン（必中）", 3000, 270, 316103, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316200, 1, "アストライアタケモドキ", 16, 0, 0, 0, 0, "", 3000, 270, 316200, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316200, 1, "アストライアタケモドキ", 16, 0, 0, 0, 0, "【物理】偽キノコで獲物を誘う、恐ろしき化け茸。\nキノコを口にした者は幻覚に囚われ、何も知らぬ\n間に大自然の餌食となってしまう。\n\n・ユニオンバーストは、敵全体に自身の物理攻撃\n　力とキャラの最大ＨＰに応じた物理割合ダメー\n　ジを与え、ＨＰが40％未満のキャラを誘惑状\n　態にする。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・自身を中心とした範囲内の敵を誘惑状態にし、\n　物理攻撃力と魔法攻撃力をバトル終了まで\n　小アップさせる。\n・前から１番目のキャラに物理大ダメージ。", 3000, 270, 316201, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316200, 1, "アストライアタケモドキ", 16, 0, 0, 0, 0, "【物理】偽キノコで獲物を誘う、恐ろしき化け茸。\nキノコを口にした者は幻覚に囚われ、何も知らぬ\n間に大自然の餌食となってしまう。\n\n・ユニオンバーストは、敵全体に自身の物理攻撃\n　力とキャラの最大ＨＰに応じた物理割合ダメー\n　ジを与え、ＨＰが40％未満のキャラを誘惑状\n　態にする。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・自身を中心とした範囲内の敵を誘惑状態にし、\n　物理攻撃力と魔法攻撃力をバトル終了まで\n　小アップさせる。\n・前から１番目のキャラに物理大ダメージ。", 3000, 270, 316202, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316200, 1, "アストライアタケモドキ", 16, 0, 0, 0, 0, "【物理】偽キノコで獲物を誘う、恐ろしき化け茸。\nキノコを口にした者は幻覚に囚われ、何も知らぬ\n間に大自然の餌食となってしまう。\n\n・ユニオンバーストは、敵全体に自身の物理攻撃\n　力とキャラの最大ＨＰに応じた物理割合ダメー\n　ジを与え、ＨＰが40％未満のキャラを誘惑状\n　態にする。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・自身を中心とした範囲内の敵を誘惑状態にし、\n　物理攻撃力と魔法攻撃力をバトル終了まで\n　大アップさせる。\n・自身のＨＰが50％以下になるとさらに攻撃が\n　激しく凶暴になる。\n・前から１番目のキャラに物理大ダメージ。", 3000, 270, 316203, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316204, 1, "アストライアタケモドキ", 16, 0, 0, 0, 0, "【ＭＯＤＥ１】林林総総たるしもべ共を従え、\n化け茸は攻勢を層一層強める。捕食者となった\n異類異形たちの反撃が今、幕を開ける。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・子分から受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせ\n　る。\n・バトル開始時に、子分を４体召喚し敵全体を\n　ノックバックさせる。\n　子分は前から１番目のキャラに物理中ダメージ\n　を与える。\n　子分を１体倒すたびに、のこり子分数が１減少\n　する。その後、子分が再度召喚され、敵全体を\n　ノックバックさせる。", 3000, 270, 316204, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316205, 1, "アストライアタケモドキ", 16, 0, 0, 0, 0, "【ＭＯＤＥ２】大自然の猛威は幻覚のみに留まら\nず、しもべは強烈な麻痺攻撃を繰り出す。その威\n力に、獲物は指先一つ動かすことすら許されない。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・アストライアタケモドキから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回復\n　量を中ダウンさせる。\n・ユニオンバーストは、敵全体に自身の物理攻撃\n　力とキャラの最大ＨＰに応じた物理割合ダメー\n　ジを与える。\n・前から１番目のキャラに物理大ダメージ。\n・子分が胞子を集めている間、アストライアタケ\n　モドキの物理防御力と魔法防御力が特大ダウン\n　する。その後、子分が胞子を放ち前方３キャラ\n　を麻痺状態にする。\n　子分が胞子を集めている間にアストライアタケ\n　モドキが700000以上のダメージを受ける\n　と、アストライアタケモドキと子分が気絶し、\n　物理防御力と魔法防御力が特大ダウンする。", 3000, 270, 316205, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316206, 1, "アストライアタケモドキ", 16, 0, 0, 0, 0, "【ＭＯＤＥ３】互いの身命が懸かった狩りの時間\nは終盤を迎える。狩るか、それとも狩られるか。\n万緑の戦野に、最後の胞子が撒き散らされる。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・アストライアタケモドキから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回復\n　量を中ダウンさせる。\n・ユニオンバーストは、敵全体に自身の物理攻撃\n　力とキャラの最大ＨＰに応じた物理割合ダメー\n　ジを与え、ＨＰが40％未満のキャラを誘惑状\n　態にする。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・自身を中心とした範囲内の敵を誘惑状態にし、\n　物理攻撃力と魔法攻撃力をバトル終了まで\n　大アップさせる。\n・自身のＨＰが50％以下になるとさらに攻撃が\n　激しく凶暴になる。\n・前から１番目のキャラに物理大ダメージ。", 3000, 270, 316206, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316207, 1, "バロン・ファンガス", 16, 0, 0, 0, 0, "【物理】化け茸を統べる気高きキノコの貴族。\n幻覚性の胞子をまき散らし、敵の精神を侵食する。\n\n・環境効果【秋高馬肥】を発動する。このスキル\n　の効果は敵、味方すべてに適用される。一定時\n　間が経過するごとに、敵、味方全体の物理攻撃\n　力、魔法攻撃力、物理防御力、魔法防御力を\n　アップさせる。この効果の効果量は敵、味方を\n　合わせた数に応じて増加する。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン50％カット\n　効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。\n・自身が一度の行動で受けるダメージ値を減衰さ\n　せる。ダメージ値が上昇するほど減衰率も上昇\n　する。\n・バトル開始時、敵全体にＴＰ上昇上限効果を付\n　与する。\n・バロン・ファンガスから受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、敵全体に自身の物理攻撃\n　力に応じた物理割合ダメージを与える。さらに、\n　キャラのＨＰが40％未満のキャラを混乱状態\n　にする。このスキルによるダメージでは、ＴＰ\n　が回復しない。このスキルの状態異常はダメー\n　ジを無効化しても付与される。\n・前方範囲内の敵に混乱状態を付与し、味方全体\n　の物理防御力と魔法防御力をバトル終了まで大\n　アップさせる。\n・子分を４体召喚し、敵全体をノックバックさせ\n　る。子分は前から１番目の敵キャラに物理中ダ\n　メージを与える。", 3000, 270, 316207, 1.6);
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (316208, 1, "バロン・ファンガス", 16, 0, 0, 0, 0, "【物理】化け茸を統べる気高きキノコの貴族。\n幻覚性の胞子をまき散らし、敵の精神を侵食する。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・敵全体に物理ダメージ（飛行効果無視）\n・対象のＨＰ70％未満：混乱（飛行効果無視・\n　必中）\n【スキル１】\n・前から敵３体に物理ダメージ（召喚体無視）\n・味方全体に物理攻撃力アップ\n・味方全体に行動速度アップ\n【スキル２】\n・自身にＨＰ回復\n・子分の数が多いほど：回復量アップ\n・子分を４体召喚\n・敵全体をノックバック\n・自身を中心に物理／魔法防御力アップフィー\n　ルド\n【通常攻撃】\n・前から敵１体に物理ダメージ\n【召喚体通常攻撃】\n・前から敵１体に物理ダメージ\n・対象のＨＰ50％未満：ノックバック（必中）", 3000, 270, 316208, 1.6);
