INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-02-15 15:00:00", 190801, 13, 1000001, 0, "2030-04-01 14:00:00", 1000, "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？");
