INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-02-15 15:00:00", 190801, 13, 1001001, 0, "2030-04-01 14:00:00", 1001, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-02-15 15:00:00", 190801, 12, 1002001, 0, "2030-04-01 14:00:00", 1002, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-02-15 15:00:00", 190801, 11, 1003001, 0, "2030-04-01 14:00:00", 1003, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-02-15 15:00:00", 190801, 3, 1004001, 0, "2030-04-01 14:00:00", 1004, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-02-15 15:00:00", 190801, 7, 1005001, 0, "2030-04-01 14:00:00", 1005, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-05-31 12:00:00", 190801, 2, 1006001, 0, "2030-04-01 14:00:00", 1006, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2020-08-15 15:00:00", 190801, 4, 1007001, 0, "2030-04-01 14:00:00", 1007, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2021-08-31 12:00:00", 190801, 6, 1008001, 0, "2030-04-01 14:00:00", 1008, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2021-12-18 15:00:00", 190801, 13, 1009001, 0, "2030-04-01 14:00:00", 1009, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2022-02-15 15:00:00", 190801, 4, 1010001, 0, "2030-04-01 14:00:00", 1010, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2022-07-18 15:00:00", 190801, 6, 1011001, 0, "2030-04-01 14:00:00", 1011, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2023-09-18 15:00:00", 190801, 6, 1012001, 0, "2030-04-01 14:00:00", 1012, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2024-04-18 15:00:00", 190801, 6, 1013001, 0, "2030-04-01 14:00:00", 1013, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪");
INSERT INTO `v1_a99ed12c516c8d66071112c678bd0e9943dfc9e3ddd0a130b1722c86f66d698b` VALUES ("2024-11-18 15:00:00", 190801, 6, 1014001, 0, "2030-04-01 14:00:00", 1014, "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪");
