INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (1, 3, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "やばいですね☆なの！", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 100, 5, "ペコリーヌのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (2, 3, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "主さま、なの！", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 101, 5, "コッコロのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (3, 3, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 102, 5, "キャルのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (4, 3, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "おにーたん♪なの！", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 103, 5, "シェフィのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (5, 3, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "ハーハッハッハ！\n世界征服なのじゃなのー！", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 104, 5, "イリヤのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (6, 3, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "お兄ちゃんのエッチ！　なの！", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 105, 5, "アカリのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (7, 3, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 106, 5, "ヨリのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (8, 3, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 107, 5, "シノブのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (9, 3, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "インフェルノシールド！なの！", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 108, 5, "ジュンのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (10, 3, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 109, 5, "トモのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (11, 3, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "タイガーヒーローボンバー！\nなの！", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 110, 5, "マツリのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (13, 3, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "ま、いいんじゃん？\nしらんけどなの", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 111, 5, "クロエのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (14, 3, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "ぷりーん★なの！", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 112, 5, "チエルのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (15, 3, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "端的に換言すれば\n青春の味なの？", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 113, 5, "ユニのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (16, 3, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "幼なじみはあんた…なの？", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 114, 5, "サレンのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (17, 3, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "ひゃああんなの～！", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 115, 5, "スズメのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (18, 3, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "サルキデバレヤ！なの！", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 116, 5, "クルミのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (19, 3, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "プリンフルスイングなの～！", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 117, 5, "アヤネのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (20, 3, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "みらくるみやりん\nくるりんぱ～☆なの！", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 118, 5, "マホのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (21, 3, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "ウルフェンバイトなの！", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 119, 5, "マコトのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (22, 3, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "なんくるないさ～！なの！", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 120, 5, "カオリのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (23, 3, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "ふふん。簡単な推理なの", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 121, 5, "カスミのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (24, 3, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "エリザベス～！なの～！", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 122, 5, "マヒルのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (25, 3, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 123, 5, "シオリのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (26, 3, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "もふもふにしてあげるなの！", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 124, 5, "リマのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (27, 3, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "めんどくさいな～なの…", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 125, 5, "リンのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (28, 3, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プリンの権能を解放するの～！", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 126, 5, "ラビリスタのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (29, 3, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "お姉ちゃんパワー全開！なの！", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 127, 5, "シズルのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (30, 3, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "お兄ちゃん、プリンをよこすの！", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 128, 5, "リノのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (31, 3, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "困った時はお互いさまさま！\nなの！", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 129, 5, "ヒヨリのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (32, 3, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "フラワリングヒールなの～♪", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 130, 5, "ユイのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (33, 3, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "スラッシュテンペスト！なの。", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 131, 5, "レイのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (34, 3, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "おーほっほほほ！なの！", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 132, 5, "アキノのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (35, 3, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリしゅわ最高～！なの！", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 133, 5, "ユカリのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (36, 3, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "にゃにゃにゃ！なの！", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 134, 5, "タマキのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (37, 3, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "効率全開！なの！", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 135, 5, "ミフユのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (38, 3, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "あらあら～なの～♪", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 136, 5, "ミサトのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (39, 3, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "スヤスヤ～…なの…", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 137, 5, "ハツネのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (40, 3, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "ぼっちは卒業なの～！", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 138, 5, "アオイのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (41, 3, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "リズムあげるのー！", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 139, 5, "ノゾミのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (42, 3, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "風の加護を！なの！", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 140, 5, "チカのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (43, 3, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "騎士さんのために\nしたんじゃありません！なの！", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 141, 5, "ツムギのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (12, 3, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "宴の始まりなの！", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 142, 5, "クリスティーナのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (44, 3, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "一刀両断、覚悟しな！なの！", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 143, 5, "ルカのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (45, 3, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "きゅぴーん！キタコレ！なの！", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 144, 5, "ナナカのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (46, 3, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "人呼んで、疾風のミヤコなの！", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 145, 5, "アンナのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (47, 3, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "クスクス…クスクスクス…なの", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 146, 5, "エリコのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (48, 3, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 147, 5, "ミツキのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (49, 3, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "私は子どもではない！なの！", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 148, 5, "モニカのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (50, 3, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 149, 5, "ニノンのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (51, 3, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "…ぐふふ、じゅるり～なの", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 150, 5, "クウカのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (52, 3, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "あぁ、ミヤコはなんて美しいの～", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 151, 5, "ユキのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (53, 3, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 152, 5, "アユミのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (54, 3, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "にひひ…イタズラの時間なの！", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 153, 5, "ミソギのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (55, 3, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ぴょんぴょこぴょーん、なの♪", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 154, 5, "ミミのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (56, 3, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "へんたいふしんしゃさんなの！", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 155, 5, "キョウカのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (57, 3, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "ハート狙い撃ち！なの！", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 156, 5, "スズナのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (58, 3, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "授業を始めます♪なの！", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 157, 5, "イオのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (59, 3, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "立派なレディ、なの！", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 158, 5, "ミサキのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (60, 3, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 159, 5, "ホマレのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (61, 3, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "たぎってきたの！", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 160, 5, "カヤのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (62, 3, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "下剋上、やってやるですなの！", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 161, 5, "イノリのプリン", 0);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (63, 3, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "どけどけ～！なの～！", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 162, 5, "ムイミのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (64, 3, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ミラーミラー！なの！", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 163, 5, "ネネカのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (65, 3, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "プリンに忠誠を誓いますわ！\nなの！", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 164, 5, "クレジッタのプリン", 1);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES (66, 3, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "ギルド管理協会へようこそ！\nなの！", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 165, 5, "カリンのプリン", 1);
