INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ペコリーヌのプリン", 5, 1, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "やばいですね☆なの！", 3, 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 100);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("コッコロのプリン", 5, 1, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "主さま、なの！", 3, 2, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 101);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("キャルのプリン", 5, 0, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3, 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 102);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("シェフィのプリン", 5, 1, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "おにーたん♪なの！", 3, 4, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 103);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("イリヤのプリン", 5, 1, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "ハーハッハッハ！\n世界征服なのじゃなのー！", 3, 5, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 104);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("アカリのプリン", 5, 1, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "お兄ちゃんのエッチ！　なの！", 3, 6, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 105);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ヨリのプリン", 5, 1, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 3, 7, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 106);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("シノブのプリン", 5, 0, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 3, 8, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 107);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ジュンのプリン", 5, 1, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "インフェルノシールド！なの！", 3, 9, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 108);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("トモのプリン", 5, 1, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 3, 10, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 109);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("マツリのプリン", 5, 1, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "タイガーヒーローボンバー！\nなの！", 3, 11, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 110);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("クロエのプリン", 5, 1, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "ま、いいんじゃん？\nしらんけどなの", 3, 13, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 111);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("チエルのプリン", 5, 1, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "ぷりーん★なの！", 3, 14, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 112);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ユニのプリン", 5, 1, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "端的に換言すれば\n青春の味なの？", 3, 15, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 113);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("サレンのプリン", 5, 1, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "幼なじみはあんた…なの？", 3, 16, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 114);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("スズメのプリン", 5, 1, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "ひゃああんなの～！", 3, 17, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 115);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("クルミのプリン", 5, 0, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "サルキデバレヤ！なの！", 3, 18, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 116);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("アヤネのプリン", 5, 1, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "プリンフルスイングなの～！", 3, 19, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 117);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("マホのプリン", 5, 1, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "みらくるみやりん\nくるりんぱ～☆なの！", 3, 20, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 118);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("マコトのプリン", 5, 1, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "ウルフェンバイトなの！", 3, 21, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 119);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("カオリのプリン", 5, 1, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "なんくるないさ～！なの！", 3, 22, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 120);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("カスミのプリン", 5, 1, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "ふふん。簡単な推理なの", 3, 23, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 121);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("マヒルのプリン", 5, 1, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "エリザベス～！なの～！", 3, 24, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 122);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("シオリのプリン", 5, 1, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 3, 25, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 123);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("リマのプリン", 5, 1, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "もふもふにしてあげるなの！", 3, 26, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 124);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("リンのプリン", 5, 0, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "めんどくさいな～なの…", 3, 27, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 125);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ラビリスタのプリン", 5, 1, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プリンの権能を解放するの～！", 3, 28, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 126);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("シズルのプリン", 5, 0, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "お姉ちゃんパワー全開！なの！", 3, 29, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 127);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("リノのプリン", 5, 1, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "お兄ちゃん、プリンをよこすの！", 3, 30, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 128);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ヒヨリのプリン", 5, 1, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "困った時はお互いさまさま！\nなの！", 3, 31, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 129);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ユイのプリン", 5, 1, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "フラワリングヒールなの～♪", 3, 32, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 130);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("レイのプリン", 5, 1, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "スラッシュテンペスト！なの。", 3, 33, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 131);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("アキノのプリン", 5, 1, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "おーほっほほほ！なの！", 3, 34, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 132);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ユカリのプリン", 5, 1, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリしゅわ最高～！なの！", 3, 35, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 133);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("タマキのプリン", 5, 1, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "にゃにゃにゃ！なの！", 3, 36, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 134);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ミフユのプリン", 5, 0, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "効率全開！なの！", 3, 37, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 135);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ミサトのプリン", 5, 1, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "あらあら～なの～♪", 3, 38, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 136);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ハツネのプリン", 5, 1, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "スヤスヤ～…なの…", 3, 39, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 137);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("アオイのプリン", 5, 1, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "ぼっちは卒業なの～！", 3, 40, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 138);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ノゾミのプリン", 5, 1, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "リズムあげるのー！", 3, 41, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 139);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("チカのプリン", 5, 1, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "風の加護を！なの！", 3, 42, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 140);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ツムギのプリン", 5, 1, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "騎士さんのために\nしたんじゃありません！なの！", 3, 43, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 141);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("クリスティーナのプリン", 5, 0, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "宴の始まりなの！", 3, 12, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 142);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ルカのプリン", 5, 1, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "一刀両断、覚悟しな！なの！", 3, 44, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 143);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ナナカのプリン", 5, 1, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "きゅぴーん！キタコレ！なの！", 3, 45, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 144);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("アンナのプリン", 5, 1, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "人呼んで、疾風のミヤコなの！", 3, 46, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 145);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("エリコのプリン", 5, 1, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "クスクス…クスクスクス…なの", 3, 47, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 146);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ミツキのプリン", 5, 1, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 3, 48, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 147);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("モニカのプリン", 5, 1, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "私は子どもではない！なの！", 3, 49, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 148);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ニノンのプリン", 5, 1, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 3, 50, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 149);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("クウカのプリン", 5, 0, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "…ぐふふ、じゅるり～なの", 3, 51, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 150);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ユキのプリン", 5, 1, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "あぁ、ミヤコはなんて美しいの～", 3, 52, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 151);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("アユミのプリン", 5, 0, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 3, 53, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 152);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ミソギのプリン", 5, 0, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "にひひ…イタズラの時間なの！", 3, 54, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 153);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ミミのプリン", 5, 0, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ぴょんぴょこぴょーん、なの♪", 3, 55, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 154);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("キョウカのプリン", 5, 0, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "へんたいふしんしゃさんなの！", 3, 56, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 155);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("スズナのプリン", 5, 1, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "ハート狙い撃ち！なの！", 3, 57, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 156);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("イオのプリン", 5, 0, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "授業を始めます♪なの！", 3, 58, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 157);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ミサキのプリン", 5, 1, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "立派なレディ、なの！", 3, 59, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 158);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ホマレのプリン", 5, 1, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 3, 60, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 159);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("カヤのプリン", 5, 1, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "たぎってきたの！", 3, 61, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 160);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("イノリのプリン", 5, 0, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "下剋上、やってやるですなの！", 3, 62, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 161);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ムイミのプリン", 5, 1, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "どけどけ～！なの～！", 3, 63, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 162);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("ネネカのプリン", 5, 1, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ミラーミラー！なの！", 3, 64, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 163);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("クレジッタのプリン", 5, 1, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "プリンに忠誠を誓いますわ！\nなの！", 3, 65, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 164);
INSERT INTO `v1_ac5f046ebc214dfe5c4e27e51743dbb7c4d2044af5f1829d763d4b41b137eef5` VALUES ("カリンのプリン", 5, 1, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "ギルド管理協会へようこそ！\nなの！", 3, 66, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 165);
