INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (101, 0.029, 0, 1.5, 1, 1, 1, 1, 0, 110, 160, 122601, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (102, 0.014, 1, 2.5, 2, 2, 3, 0, 0, 105, 120, 122401, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 111);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (103, 0.017, 0, 2.5, 3, 3, 2, 0, 1, 110, 350, 122501, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (104, 0.016, 1, 1.5, 4, 4, 3, 0, 0, 110, 130, 107501, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (105, 0.014, 0, 2.5, 5, 5, 1, 1, 0, 110, 160, 107601, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (106, 0.029, 0, 3.5, 6, 6, 2, 0, 1, 120, 255, 107801, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 103);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (107, 0.013, 0, 3.5, 7, 7, 1, 1, 0, 120, 160, 110601, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 103);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (108, 0.014, 0, 2.5, 8, 8, 2, 0, 1, 110, 350, 110401, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 103);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (109, 0.012, 1, 1.5, 9, 9, 3, 0, 0, 105, 130, 110501, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 100);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (110, 0.014, 0, 2.5, 10, 10, 1, 1, 0, 105, 200, 115701, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 103);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (111, 0.071, 0, 2.5, 11, 11, 2, 0, 1, 105, 350, 113601, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 103);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (112, 0.014, 1, 1.5, 12, 12, 3, 0, 0, 120, 130, 107901, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 103);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (113, 0.022, 0, 1.5, 13, 13, 2, 1, 0, 110, 200, 108001, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (114, 0.031, 0, 2.5, 14, 14, 1, 1, 0, 110, 160, 110301, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 108);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (115, 0.036, 1, 0.5, 15, 15, 1, 0, 0, 110, 130, 107701, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (116, 0.048, 0, 3.5, 16, 16, 2, 0, 1, 110, 350, 113101, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 95);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (117, 0.043, 1, 1.5, 17, 17, 3, 0, 0, 105, 130, 113301, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (118, 0.018, 1, 1.5, 18, 18, 2, 0, 0, 105, 130, 113201, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 95);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (119, 0.021, 0, 2.5, 19, 19, 2, 1, 0, 110, 160, 117001, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 108);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (120, 0.036, 0, 2.5, 20, 20, 1, 0, 1, 110, 255, 113501, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (121, 0.02, 0, 2.5, 21, 21, 3, 0, 1, 105, 350, 113401, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (122, 0.018, 1, 2.5, 22, 22, 1, 0, 0, 110, 130, 110101, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (123, 0.03, 0, 3.5, 23, 23, 3, 1, 0, 105, 160, 110001, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 111);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (124, 0.027, 1, 1.5, 24, 24, 1, 0, 0, 110, 130, 117201, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 103);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (125, 0.02, 0, 3.5, 25, 25, 2, 1, 0, 105, 160, 117301, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 108);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (126, 0.04, 0, 2.5, 26, 26, 3, 0, 1, 110, 350, 117401, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (127, 0.009, 0, 3.5, 27, 27, 1, 0, 1, 110, 350, 117101, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (128, 0.022, 0, 3.5, 28, 28, 1, 0, 1, 120, 255, 122901, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (129, 0.028, 1, 2.5, 29, 29, 3, 0, 0, 110, 130, 122801, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 105);
INSERT INTO `v1_acf7fca8d2b752ed44055de2c20e8503152a8b7ceb0d4edb675ed314134c43c5` VALUES (130, 0.023, 0, 3.5, 30, 30, 2, 1, 0, 105, 130, 122701, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 108);
