INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ペコリーヌのプリン", 100, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "やばいですね☆なの！", 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "コッコロのプリン", 101, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "主さま、なの！", 2, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "キャルのプリン", 102, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "シェフィのプリン", 103, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "おにーたん♪なの！", 4, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "イリヤのプリン", 104, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "ハーハッハッハ！\n世界征服なのじゃなのー！", 5, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "アカリのプリン", 105, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "お兄ちゃんのエッチ！　なの！", 6, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ヨリのプリン", 106, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 7, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "シノブのプリン", 107, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 8, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ジュンのプリン", 108, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "インフェルノシールド！なの！", 9, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "トモのプリン", 109, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 10, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "マツリのプリン", 110, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "タイガーヒーローボンバー！\nなの！", 11, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "クロエのプリン", 111, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "ま、いいんじゃん？\nしらんけどなの", 13, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "チエルのプリン", 112, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "ぷりーん★なの！", 14, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ユニのプリン", 113, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "端的に換言すれば\n青春の味なの？", 15, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "サレンのプリン", 114, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "幼なじみはあんた…なの？", 16, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "スズメのプリン", 115, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "ひゃああんなの～！", 17, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "クルミのプリン", 116, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "サルキデバレヤ！なの！", 18, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "アヤネのプリン", 117, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "プリンフルスイングなの～！", 19, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "マホのプリン", 118, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "みらくるみやりん\nくるりんぱ～☆なの！", 20, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "マコトのプリン", 119, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "ウルフェンバイトなの！", 21, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "カオリのプリン", 120, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "なんくるないさ～！なの！", 22, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "カスミのプリン", 121, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "ふふん。簡単な推理なの", 23, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "マヒルのプリン", 122, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "エリザベス～！なの～！", 24, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "シオリのプリン", 123, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 25, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "リマのプリン", 124, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "もふもふにしてあげるなの！", 26, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "リンのプリン", 125, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "めんどくさいな～なの…", 27, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ラビリスタのプリン", 126, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プリンの権能を解放するの～！", 28, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "シズルのプリン", 127, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "お姉ちゃんパワー全開！なの！", 29, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "リノのプリン", 128, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "お兄ちゃん、プリンをよこすの！", 30, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ヒヨリのプリン", 129, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "困った時はお互いさまさま！\nなの！", 31, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ユイのプリン", 130, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "フラワリングヒールなの～♪", 32, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "レイのプリン", 131, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "スラッシュテンペスト！なの。", 33, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "アキノのプリン", 132, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "おーほっほほほ！なの！", 34, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ユカリのプリン", 133, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリしゅわ最高～！なの！", 35, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "タマキのプリン", 134, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "にゃにゃにゃ！なの！", 36, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ミフユのプリン", 135, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "効率全開！なの！", 37, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ミサトのプリン", 136, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "あらあら～なの～♪", 38, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ハツネのプリン", 137, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "スヤスヤ～…なの…", 39, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "アオイのプリン", 138, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "ぼっちは卒業なの～！", 40, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ノゾミのプリン", 139, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "リズムあげるのー！", 41, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "チカのプリン", 140, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "風の加護を！なの！", 42, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ツムギのプリン", 141, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "騎士さんのために\nしたんじゃありません！なの！", 43, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "クリスティーナのプリン", 142, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "宴の始まりなの！", 12, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ルカのプリン", 143, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "一刀両断、覚悟しな！なの！", 44, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ナナカのプリン", 144, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "きゅぴーん！キタコレ！なの！", 45, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "アンナのプリン", 145, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "人呼んで、疾風のミヤコなの！", 46, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "エリコのプリン", 146, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "クスクス…クスクスクス…なの", 47, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ミツキのプリン", 147, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 48, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "モニカのプリン", 148, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "私は子どもではない！なの！", 49, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ニノンのプリン", 149, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 50, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "クウカのプリン", 150, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "…ぐふふ、じゅるり～なの", 51, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ユキのプリン", 151, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "あぁ、ミヤコはなんて美しいの～", 52, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "アユミのプリン", 152, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 53, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ミソギのプリン", 153, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "にひひ…イタズラの時間なの！", 54, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ミミのプリン", 154, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ぴょんぴょこぴょーん、なの♪", 55, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "キョウカのプリン", 155, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "へんたいふしんしゃさんなの！", 56, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "スズナのプリン", 156, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "ハート狙い撃ち！なの！", 57, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "イオのプリン", 157, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "授業を始めます♪なの！", 58, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ミサキのプリン", 158, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "立派なレディ、なの！", 59, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ホマレのプリン", 159, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 60, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "カヤのプリン", 160, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "たぎってきたの！", 61, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "イノリのプリン", 161, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "下剋上、やってやるですなの！", 62, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 0);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ムイミのプリン", 162, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "どけどけ～！なの～！", 63, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "ネネカのプリン", 163, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ミラーミラー！なの！", 64, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "クレジッタのプリン", 164, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "プリンに忠誠を誓いますわ！\nなの！", 65, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 1);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, 3, "カリンのプリン", 165, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "ギルド管理協会へようこそ！\nなの！", 66, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 1);
